css:
@-moz-document url-prefix("http://forum.itpc.net.pl/") {
/*TŁO GŁÓWNE*/
a:link {color:#FFFFFF !important;}
a:visited {color:#FFFFFF !important;}
a:hover {color:#FFFFFF !important;}
a:active {color:#FFFFFF !important;}

/*komentarz*/
/*display:none; usuwa objekt*/


html, body {
    background-color: #242424 !important;
  	color: #FFFFFF !important;
}


.ipsType_pagetitle, .ipsType_subtitle {
    font: 300 26px/1.3 Helvetica,Arial,sans-serif !important;
    color: #f6f6f6 !important;
}

/*Rozwijany box od nicku */
.ipsHeaderMenu {
    background: #242424 !important;
}

/*Podswietlenia wcisnietego klawisza od nicku*/
#user_link.menu_active, #notify_link.menu_active, #inbox_link.menu_active {

    background: #333 !important;
}

/*TŁO GŁÓWNE*/


/*Ustawienia forum >> tło tabelki----------------------------------------*/
.ipsVerticalTabbed_tabs li {
    background-color: #242424 !important;
}
  
 /*Kolor statusu usera*/
  #user_latest_status > div {
    color: #FFFFFF !important;
    background-color: #5c5c5c !important; 
    padding-left: 0px !important;
  }

.status_update {
   background: #333 !important;
}

/*Obwódka FORUM*/
.maintitle {
    background: #242424 !important;
}

/*Kolor wypowiedzi na forum*/

/* 1.Tło postu>>>>>*/
.post_body {
    background: #242424 !important;
    color: #FFFFFF !important;
}

/* 2.Tło obramowanie postu>>>*/
.post_block {
    background: #242424 !important;
}
/* 3. Coś tam ale działa */
.post_block h3 {
    background: rgba(97, 97, 97, 0.24) !important;;
}

  /* div b, div strong, div p, div span.row_title , div span , , */
div.post.entry-content, div p  {color: #FFFFFF !important;} 
div.fixed_inner.ipsPad.row1 { background: #242424 !important; }

  
/* 4.Kolor czcionki */
.post_body .post {
  
  line-height: 1.6 !important;
    font-size: 14px !important;
    word-wrap: break-word !important;
}
  
.blockquote .ipsBlockquote {
    background: rgba(97, 97, 97, 0.24) !important;;
}
/* 5.Kolor czcionki w cytacie */
blockquote.ipsBlockquote {
    color: #ff9800 !important;
    background: rgba(97, 97, 97, 0.24) !important;; 
}
/* 6.Pierwsze tło cytatu */
p.citation {
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#f1831d), color-stop(100%,#e21a1a)) !important; border: 1px solid #dbdbdb !important; color: #FFFFFF !important;
}

.message {
    border: 1px solid #FFFFFF !important;
    background: #5c5c5c !important;
}

#message_table tr:nth-child(2n+1) {
background: #333 !important;
}

.no_messages {
background: #333 !important;
}

h3.ipsSettings_sectiontitle {
    color: #FFFFFF !important;
}

.cke_show_borders {
    color: #FFFFFF !important;
}
  
.general_box strong {
	background: #5c5c5c !important;
color: #FFFFFF !important;
}

.ipsType_pagetitle, .ipsType_subtitle {
color: #FFFFFF !important;
}

.ipsPad, .bar {
background: #242424 !important;
}

.clearfix {
background: #242424 !important;
}
  
.general_box h3 {
  background: #5c5c5c !important;
  color: #FFFFFF  !important;
}
  
.ipsVerticalTabbed_tabs li.active a {
  background: #242424 !important;
}
  
.ipsTag {
  background: #000000  !important;
}
  

/*Ustawienia forum >> tło tabelki----------------------------------------*/





    /*całe elementy---------------------------------------------------------*/
#banerek {
    display:none !important;
}

#branding {
background: #242424 !important;
}

#content {
    max-width: 87%!important;
    min-width: 980px!important;
    margin-left: auto!important;
    margin-right: auto!important;
    background: #242424!important;
}

#stopka {
    background: #242424 !important;
}

#ipbwrapper {
     background: #242424 !important;
}
#document { 
    background: #FFFFFF !important;
}

#tlo-obrazek {
    display: none !important;
}

#cookies {
display: none !important;
}

body#ipboard_body fieldset.submit, body#ipboard_body p.submit {
    background-color: #242424 !important;
}

 
/*całe elementy---------------------------------------------------------*/


/*Tabelka nowa zawartość------------------------------------------*/

/* 1.Kolor obwódki*/
.ipsSideMenu {
border-bottom: rgba(255, 255, 255, 0.75) !important;
}

/* 2.Kolor tła*/
.ipsLayout_left {
    background: #242424 !important;;
}

    /* 3.Kolor czcionki*/
.ipsSideMenu h4 {
    color: #FFFFFF !important;
}

/* 4. Pierwsze tło tematów*/
._recordRow {
    background: rgba(255,255,255,0.1) !important;;
}
/* 5.Drugie tło tematów*/
._recordRow:nth-child(4n+3) {
	background: #525252 !important;
}

/* 6.Kolor czcionki na wyświetleniach*/
.col_f_views {
    color: #FFFFFF !important;
	
}

/*Tabelka nowa zawartość------------------------------------------*/





    /*PRZYCISKI*/

/* 1. Obserwuj ten temat*/
.ipsButton_secondary {
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#f1831d), color-stop(100%,#e21a1a)) !important; 
    color: #FFFFFF !important
}

/* 2.Dodaj odpowiedź */
.topic_buttons li.important a, .topic_buttons li.important span, .ipsButton .important, .topic_buttons li a, .topic_buttons li span, .ipsButton {
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#f1831d), color-stop(100%,#e21a1a)) !important; border: 1px solid #dbdbdb !important;
}

ul.post_controls a {
    color: #FFFFFF !important;
}

/* 3.Przyciski stron 1,2,3.. i nastepna */

.pagination .back a, .pagination .forward a {
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#f1831d), color-stop(100%,#e21a1a)) !important; border: 1px solid #dbdbdb !important; color: #FFFFFF !important;
}

.pagination .pages li.active {
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#f1831d), color-stop(100%,#e21a1a)) !important; border: 1px solid #dbdbdb !important; color: #FFFFFF !important;
}
.pagination .pages a, .pagejump {
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#f1831d), color-stop(100%,#e21a1a)) !important; border: 1px solid #dbdbdb !important; color: #FFFFFF !important;
}

ul.mini_pagination li a{
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#f1831d), color-stop(100%,#e21a1a)) !important; border: 1px solid #dbdbdb !important; color: #FFFFFF !important;
}
/* 4.Więcej opcji*/
.input_submit.alt {
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#f1831d), color-stop(100%,#e21a1a)) !important; border: 1px solid #dbdbdb !important; color: #FFFFFF !important;
}

/* 5.Napisz*/
.input_submit {
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#f1831d), color-stop(100%,#e21a1a)) !important; border: 1px solid #dbdbdb !important; color: #FFFFFF !important;
}


#community_app_menu > li#primary_nav  {
    background: #242424 !important;
}

a#itpc {
 background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#f1831d), color-stop(100%,#e21a1a)) !important; border: 1px solid #dbdbdb !important; color: #FFFFFF !important;
}




.topic_buttons li.non_button a  {
     background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#f1831d), color-stop(100%,#e21a1a)) !important; 
    background-color: transparent !important;
    border: 1px solid #dbdbdb !important; 
    box-shadow: none !important;
    -moz-box-shadow: none !important;
    -webkit-box-shadow: none !important;
    text-shadow: none !important;
    min-width: 0px !important;
    color: #FFFFFF !important;
    font-weight: normal !important;
}
  



/*PRZYCISKI*/


    /* Strona główna */

.row1 {
    background: rgba(255,255,255,0.1) !important;
}

.row2 {
	background: #5c5c5c !important;
}
  
.hover_rows tr:nth-child(2n+1) {
  background: #5c5c5c !important;
}
    
#board_stats  {
    background: #242424 !important;
}

#board_statistics  {
    background: #242424 !important;
}

/*Panel logowania*/
.popupInner h3 {
   background: #242424 !important;
}
.ipsForm_submit {
   background: #333 !important;
}
.popupInner {
   background: #333 !important;
}

}
