css:
@-moz-document domain("userstyles.org") {
html, body, div, span, applet, object, iframe, h1, h2, h3, h4, h5, h6, p, blockquote, pre, a, abbr, acronym, address, big, cite, code, del, dfn, em, img, ins, kbd, q, s, samp, small, strike, strong, sub, sup, tt, var, b, u, i, center, dl, dt, dd, ol, ul, li, fieldset, form, label, legend, table, caption, tbody, tfoot, thead, tr, th, td, article, aside, canvas, details, embed, figure, figcaption, footer, header, hgroup, menu, nav, output, ruby, section, summary, time, mark, audio, video {
    border: 0;
    font: inherit;
    font-size: 100%;
    margin: 0;
    padding: 0;
    vertical-align: baseline;
}
article, aside, details, figcaption, figure, footer, header, hgroup, menu, nav, section {
    display: block;
}
body {
    line-height: 1;
}
ol, ul {
    list-style: none;
}
blockquote, q {
    quotes: none;
}
blockquote:before, blockquote:after, q:before, q:after {
    content: '';
    content: none;
}
table {
    border-collapse: collapse;
    border-spacing: 0;
}
* {
    font-family: Arial, Helvetica, sans-serif !important;
}
a, a:hover, a:visited, a:hover:visited {
    color: #0FADED;
    cursor: pointer;
    text-decoration: none;
}
html {
    background: #333;
    height: 100%;
    margin: 0;
    padding: 0;
}
body {
    background: #333;
    max-width: 100% !important;
    min-height: 100%;
    padding-bottom: 4em;
    width: 100%;
}
article {
	background-color: #292929;
	margin-bottom: 1em !important;
	padding: 1em;
}
article > .style-brief-text > .style-brief-stats {
	color: #0FADED;
	margin-top: 1em;
}
article > .style-brief-text > header {
	font-size: 1.5em;
	margin-bottom: .5em;
}
article > .style-brief-text > header > a {
	color: #0FADED;
}
article > .style-brief-text > header > a:hover {
	color: #0FADED;
}
article > .style-brief-text > p {
	font-size: 1.3em;
	line-height: 1.3em;
	overflow-wrap: break-word;
	padding: .5em;
}
body::-webkit-scrollbar {
	background-color: #333;
	width: 1em;
}
body::-webkit-scrollbar-thumb {
	background-color: #444;
	outline: 1px solid #333;
}
body::-webkit-scrollbar-track {
	box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.3);
}
footer {
	background-color: #292929;
	bottom: 0px;
	height: 4em;
	margin-top: 1em;
	padding: 1em 0;
	position: fixed;
	width: 100%;
}
footer > font > p {
	display: inline-block;
	font-size: 100%;
	padding: 1em .05em;
}
footer > font > p > a {
	background-color: #444;
	box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2);
	padding: 1em;
}
footer > font > p > a:hover {
	background-color: #333;
}
footer > font > p:first-of-type {
	margin-left: 1em;
}
input {
	background-color: #DDD;
	border: 0;
	outline: none;
}
input[type='checkbox']:after {
	background: rgb(224, 224, 224);
	background-color: #222 !important;
	border: 1px solid #333;
	border-radius: 0;
	box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2);
	content: '';
	display: inline-block;
	height: 18px;
	line-height: 1.5em;
	margin-left: -4px;
	margin-top: -4px;
	width: 18px;
}
input[type='checkbox']:checked:after {
	background-color: #0FADED !important;
	border: 2px solid #333;
	height: 15px;
	width: 15px;
}
input[type=radio] {
	cursor: pointer;
	height: 1.1em;
	margin-left: 5px;
	position: relative;
	visibility: hidden;
	width: 1.1em;
}
input[type=radio]:before {
	border: 2px solid #444;
	border-radius: 50%;
	bottom: 0;
	content: "";
	left: 0;
	position: absolute;
	right: 0;
	top: 0;
	visibility: visible;
}
input[type=radio]:checked:before {
	background-color: #0FADED;
}
textarea {
	background-color: #DDD;
	border: 0;
	outline: none;
}
@media only screen and (max-width: 950px) {
	#main-style-info {
		margin: 1em 0;
		padding: 0;
		width: 100%;
	}
	#main-style-info > #long-description {
		padding: 1em;
	}
}
@media only screen and (max-width: 950px) {
	#content-wrapper {
		margin-top: .25em;
	}
