css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("meneame.net") {

  /**
   * Cabecera superior
   */
  
  .header-top-wrapper {
    background-color: #f0743a;
  }

  
  /**
   * Sub actual
   */
  
  #header-top a.sub-name {
    color: rgba(255,255,255,.85) !important;
  }
  
  
  /**
   * Botón "Enviar Historia"
   */
  
  .menu01-itemsl a.submit_new_post {
    background: #f8f8f8 !important;
    color: #f0743a !important;
  }
  
  .header-menu01 ul li a {
    color: #222 !important;
  }
  
  
  /**
   * Cajas de contenido laterales
   */
  
  /* Descripción del sub */
  .sidebox-sub .description {
    color: #333 !important;
  }
  
  /* En subs */
  .sidebox.brown .header {
    background: #f0743a !important;
  }
  
  /* Pregúntame */
  .sidebox-rounded.orange .header {
    background: #f0743a !important;
  }
  
  .sidebox-rounded a.show-responses {
    color: #f0743a !important;
  }
  
  /* Destacadas */
  .sidebox.red .header {
    background-color: #c33c49 !important;
  }
  
  .sidebox.red .votes span, .sidebox.red .clics span {
    border: 2px solid #c33c49 !important;
    color: #c33c49 !important;
  }

  /* Más visitadas */
  .sidebox .votes span, .sidebox .clics span {
    border: 2px solid #f0743a !important;
  }
  
  
  /**
   * Barra de subs
   */
  
  #header-center span, #header-center a.discover-subs {
    color: #ddd  !important;
  }

  ul.official-subs li a {
    color: #ddd !important;
  }
  
  .header-center-wrapper {
    background-color: #222 !important;
  }
  
  
  /**
   * Botón "menéalo"
   */
  
  .menealo a {
    box-shadow: 0 1px 2px 0 rgba(196, 62, 11, .65) !important;
  }
  
  .menealo span {
    border: 1px solid #ff6300 !important;
    background: none !important;  
    box-shadow: 0 1px 2px 0 rgba(196, 62, 11, .65) !important;
    color: #ff6300 !important;
  }

  
  /**
   * Notificaciones
   */
  
  ul#userinfo a.notifications span {
    color: #222 !important;
  }
  
  
  /**
   * Barra de detalles de la noticia
   */
  
  .news-details {
    border-bottom: 0 !important;
    border-top: 0 !important;
  }
  
  
  /**
   * Sugerencias de @Kaphax
   */
  
  /* Mantener la cabecera superior siempre arriba */
  .header-top-wrapper.fixed {
    position: relative;
  }
  
  /* Mostrar el icono para desconectarse */
  #header-top .hide {
    display: list-item !important;
  }
  
  
  /**
   * Sugerencia de @dogday
   */
  
  /* Avatares cuadrados */
  .avatar, .news-submitted img {
    border: 0px !important;
    border-radius: 0% !important;
  }
  
}
