css:
@-moz-document domain("douban.com") {
.status-item .hd .text .lnk-people {
    font-weight: normal;
}

/*转播阴影*/

.status-real-wrapper,
.status-real-wrapper.say {
    cursor: pointer;
    background: rgba(252, 252, 252, 0.41);
    /* border-style: dashed; */
    /* border-width: 1px; */
    /* border-color: rgba(214, 214, 214, 0.55); */
    
    box-shadow: 0px 1px 10px #f5f5f5;
    margin-right: 5px;
}

.status-real-wrapper .status-item .status-saying {
    margin-left: 0;
}

.status-item .mod a.reshared_by {
    color: #808080;
    font-family: Gentium Book Basic, 方正仿宋_GBK;
}

.status-item .mod a.reshared_by.more-reshare {
    color: #83a2c0;
    font-family: simsun;
    font-size: 0.9em;
}

/*转播对象头像-显示*/

.status-real-wrapper .usr-pic {
    display: inline!important;
}

/*转播对象名称-颜色*/

.status-real-wrapper .mod .hd a.lnk-people,
.status-real-wrapper .mod .hd a.lnk-people:link {
    color: #808080;
}

.status-real-wrapper .mod {
    margin-bottom: 0px;
}

.dialog-reshare-status-bd .mod .bd blockquote,
.status-real-wrapper .mod .bd blockquote {
    padding: 0 0.2em;
}

.status-real-wrapper .status-item .mod blockquote p {
    padding: 0;
}

/*避免转播里的图片放大后显示不全*/
.status-real-wrapper .status-item .mod .attachments-saying{
    padding-left:0;
}
.status-real-wrapper .status-item .mod .group-pics-small .group-pic img{
    max-width:440px;
}

.status-reshared-wrapper.from-detail .status-real-wrapper .status-item .mod .actions:not(:empty) {
    display: block!important;
    position: inherit;
    margin-top: 1em;
    text-align: left;
    border: none;
    bottom: 0px;
}

.status-real-wrapper .status-item .actions {
    display: block!important;
    position: inherit;
    border: none;
    padding-bottom: 0.2em;
}

.status-real-wrapper .status-item .bd {
    padding: 0px;
    padding-left: 20px;
}
}
