css:
/*
    Youtube dark Theme by TheRox
    Author: https://github.com/NyushaDEV/Youtube-Night-Theme/
*/
@-moz-document url-prefix("https://www.youtube.com"), url-prefix("http://www.youtube.com") {
/* i really want this to be global */
body {
    background: #292929;
    color: white
}

#yt-masthead #logo-container .logo {
    background: url(http://i.imgur.com/u3VbodM.png) no-repeat;
    background-size: auto;
    width: 136px;
    height: 39px;
}

#yt-masthead-container {
    background: #333;
}
#page-container {
    margin-top: 30px;
}
#masthead-appbar {
    background-color: #111111;

}
.yt-card {
    background: #1f1f1f;
}
.yt-lockup {
    color: #fff;
}
.yt-badge {
    background: white;
}
.yt-lockup-title a, .yt-lockup:hover a, .yt-lockup:hover .yt-lockup-meta a, .yt-lockup:hover .yt-lockup-description a {
    color: white;
}
a {
    background: transparent !important;
    color: white

}
a:hover {
    color: #ddd !important;
}
.yt-lockup .yt-lockup-meta a, .yt-lockup .yt-lockup-description a, .yt-lockup .yt-lockup-byline a {
    color:#2e2e2e;
}
.yt-lockup .yt-lockup-meta a, .yt-lockup .yt-lockup-description a, .yt-lockup .yt-lockup-byline a:hover {
    color:#000;
}

.branded-page-module-title a:visited, .branded-page-module-title a {
    color: #333;
    font-size: 14px;
    text-transform: uppercase;
    border-bottom: 1px solid #333;
    width: 100%;
    padding-bottom: -1px;
}
#watch-headline-title {
    color: #b5b5b5;
}
.video-list .video-list-item .title {
    color: #F44336;
    font-size: 14px;
    font-weight: 500;
}
#watch8-action-buttons .yt-uix-button, #watch8-action-buttons .yt-uix-button:hover, #action-panel-dismiss, #action-panel-dismiss:hover {
    color: white;
}
#action-panel-details a {
    color: #e74235;
}
#action-panel-details a:hover {
    color: #d03427;
}
.comment-section-header-renderer {
    padding: 0 0 20px;
    text-transform: uppercase;
    font-size: 13px;
    color: #dedede;
}
.comment-renderer-reply {
    color: #fff;

}
#watch-header .yt-user-info a {
    color: #dcdcdc;
}
#watch-description {
    color: #d8d8d8;
}
.comment-simplebox {
    background-color: transparent;
}

.yt-uix-clickcard-card.yt-masthead-multilogin .yt-masthead-picker-header, .yt-uix-clickcard-card.yt-masthead-multilogin .yt-masthead-multilogin-users-header {
    color: #383838;
}
.comment-simplebox-text:focus {
    outline: none;
}
.yt-uix-clickcard-card.yt-masthead-multilogin .yt-uix-card-body-arrow {
    border-bottom-color: #fff;
}
#masthead-search-terms {
    background-color: #1d1d1d;
}

#masthead-search-terms input {
    background: transparent;
    border: 0px solid transparent;
    display: inline-block;
    font-size: 16px;
    height: 100%;
    left: 0;
    margin: 0;
    outline: none;
    padding: 2px 6px;
    position: absolute;
    width: 100%;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    outline: hidden;
}
#masthead-search-terms:focus {
    outline: none;
}

#watch7-sidebar .video-list-item a:visited .title, #watch7-sidebar .video-list-item a:visited .title .yt-deemphasized-text {
    color: #717171;
}
a.yt-uix-button-epic-nav-item.selected, a.yt-uix-button-epic-nav-item.yt-uix-button-toggled, button.yt-uix-button-epic-nav-item.selected, button.yt-uix-button-epic-nav-item.yt-uix-button-toggled, .epic-nav-item.selected, .epic-nav-item.yt-uix-button-toggled, .epic-nav-item-heading {
    color: #F44336;
    font-weight: 500;
}
#appbar-nav .yt-uix-button.yt-uix-button-epic-nav-item {
    color: #e0e0e0;
}
.webkit .yt-ui-ellipsis-2 {
    -webkit-line-clamp: 2;
    background: transparent;
}
.yt-lockup-byline a:hover {
    color: #e5e5e5;
}
.yt-lockup .yt-lockup-meta a, .yt-lockup .yt-lockup-description a, .yt-lockup .yt-lockup-byline a {
    color: #d4d4d4;
}
.yt-lockup .yt-lockup-meta a:hover, .yt-lockup .yt-lockup-description a:hover, .yt-lockup .yt-lockup-byline a:hover {
    color: #d4d4d4;
}
.branded-page-module-title a:visited, .branded-page-module-title a {
    color: #cacaca;
}

/* Creator Studio */
#creator-sidebar .creator-sidebar-branding h1 {
    color: #d4d4d4;
}

#creator-sidebar .creator-sidebar-section a {
    display: block;
    line-height: 24px;
    font-size: 11px;
    color: #d6d6d6;
}

#creator-sidebar .creator-sidebar-section a:hover {
    color: white;
} 
label.single-option {
    display: block;
    color: black;
}
label {
    color: #b5b5b5;
}

#dashboard-header h2 {
    text-shadow: none !important;
    color: #fff !important;

}
#dashboard-header h2 a {
    color: #fff;
    box-shadow: none;
}
.channel-header .branded-page-header-title .branded-page-header-title-link {
    color: #cecece;
}
#appbar-guide-menu, .guide-flyout {
background: #111111;

}
.guide-item {
    color: #e9e9e9;
}
.eow-description:hover + a {
    color: white;
}

#creator-subheader h2 {
    color: #fff;

}
.account-content {
    margin: 10px 25px 30px;
    padding-bottom: 10px;
    line-height: 1.2;
    color: #f1f1f1;
}
.yt-card .yt-uix-button-expander {
    color: #e1e1e1;
}
.yt-card .yt-uix-button-expander:hover {
    color: #e1e1e1;
}

.yt-uix-button-default, .yt-uix-button-default[disabled], .yt-uix-button-default[disabled]:hover, .yt-uix-button-default[disabled]:active, .yt-uix-button-default[disabled]:focus {
    border-color: #646464;
    background: #111111;
    color: #fff;
}
#masthead-search {
    margin: 0 auto;
    background: rgba(0,0,0,0.2);
    transition: .3s background;
}

#masthead-search-terms {
    background-color: #111111;
}
#masthead-search-terms {
    background-color: #111111;
    font-size: 14px;
    height: 34px;
    line-height: 30px;
    margin: 0 0 2px;
    box-shadow: none;
    overflow: hidden;
    position: relative;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    -moz-transition: border-color .2s ease;
    -webkit-transition: border-color .2s ease;
    transition: border-color .2s ease;
}

.yt-uix-button-default, .yt-uix-button-default[disabled], .yt-uix-button-default[disabled]:hover, .yt-uix-button-default[disabled]:active, .yt-uix-button-default[disabled]:focus {
    border-color: #646464;
    background: #111111;
    color: #fff;
}
.yt-uix-button {
    display: inline-block;
    height: 34px !important;
}

}
