/*@namespace url(http://www.w3.org/1999/xhtml);*/  /*This was added as default with any blank stylish template. Not sure if commenting it breaks anything, so far nothing.*/

/*  Original code written by NightCross00 from the Listen.moe forums 
 This code was originally written on Dec 21st 2016.
 Purpose: To convert the theme of the day-time Listen.moe to a easier darker theme for night-time viewing.
 Version 1.0.1
*/



/*
  Changelog

Version 1.0.1
-Fixed Read Thread title color
-RELEASED to the PUBLIC.

Version 1.0 
-Base Release. 
*/


@-moz-document domain("forum.listen.moe") {

  body {
    background-color: #777;    
  }
  
  .sideNav .Dropdown--select .Dropdown-menu > li > a {
    color: white;
  }
  
  
  
  
  /* Main forum hover over threads */
  .DiscussionListItem:hover {
    background: #686868; 
  }
  
  /* Bla bla bla replied X days ago */
  .DiscussionListItem-content {
    color: #4981ce;
  }
  
  /* Preview text of thread post */
  .DiscussionListItem-info .item-excerpt {
     color: #fff;
  }
  
  /* Read Forum Posts */
  .read .DiscussionListItem-title {
    color: #383838;
  }
  
  /* "User started x days/hours ago" */
  .DiscussionListItem-main {
    color: #32ea2d;
  }
  
  
  
  /* Forum Thread */
  .Post-header, .Post-header a{
    color: white;
  }
  
  .EventPost, .EventPost a{
    color: white;
  }
  
  .ReplyPlaceholder .Post-header {
    color: white;
  }
  
  .Scrubber a{
    color: white;
  }
  
  
  
  /* Write a reply border color */
  .ReplyPlaceholder {
    border-color: rgba(183, 183, 183, 0.8);
  }
  
  
  /* Post a reply */
  .Composer-content {
    background-color: #a7a7a7;
  }
  
  /* Reply box text title */
  .ComposerBody-header h3 {
    color: #0046a2;
  }
  
  
  
  
  
  
  
  
  
  
  
  
}
