@-moz-document domain("tuwel.tuwien.ac.at") {
/*[[HEAD]]*/
/* variables */
html {
    --background-color: rgb(67, 72, 88);
    --window-color: rgb(77, 82, 98);
    --shadow-color: rgb(57, 62, 78);
    --text-color: rgb(225, 225, 225);
    --textWhite-color: rgb(255, 255, 255);
    --header-color: #393e4e;
    --headerBorder-color: #252525;
}

.modal {
    background-color: var(--window-color); 
}
.modal-container .modal .modal-header {
    color: white;
    text-align: center;
    text-shadow: none;
    border-bottom: 1px solid var(--header-color);
    background: #ccc;
    background-color: var(--header-color);
    background-image: none;
    background-repeat: none;
}
.modal-container .modal .modal-footer {
    border-top: 1px solid var(--header-color);
    background-color: #4d5262;
}

#block-region-side-pre span {
    color: var(--text-color) !important; 
}

.message .messagearea .messagehistory .right,
.message .messagearea .messagehistory .left {
 	  background-color: var(--window-color); 
}

/* code */
body {
    background-color: var(--background-color);
    color: var(--text-color);
}

.navbar-inverse .navbar-inner, .navbar-inner {
    /*background-color: var(--header-color) !important;*/
    border-top: 1px solid var(--headerBorder-color) !important;
    border-bottom: 1px solid var(--headerBorder-color) !important;
}
.alert-neutral {
    background-color: var(--window-color) !important;
    border: none;
    box-shadow: 2px 2px 0 var(--shadow-color) !important;
}
#page-header .alert h1 {
    color: var(--text-color);
}
.path-mod-grouptool .late,
.path-my .late {
    color: #C0392B;
}
#page span {
    /* color: var(--text-color) !important; */
    
    background-color: rgba(0, 0, 0, 0) !important;
}
a > span {
    color: #75a7bf !important;
}
ul {
    color: var(--text-color) !important;
}
#newmessageoverlay {
    background-color: var(--window-color);
    border-color: var(--header-color);
}
#page-site-index.notloggedin .sitetopic table td img {
    border: 1px solid var(--header-color);
}
.mform legend a,
.mform legend a:hover {
    color: #75a7bf !important;
}
/* kurs */

.course_category_tree .category>.info {
    background-color: var(--window-color);
    border-color: var(--header-color);
}
.courses .coursebox.even {
    background-color: var(--window-color);
}
.courses .coursebox.collapsed {
    border: 1px solid var(--background-color);
}
.courses .coursebox:hover {
    background-color: var(--header-color);
}
/* menue */

.dropdown-menu {
    background-color: var(--window-color);
}
.dropdown-menu .divider {
    background-color: var(--header-color);
    border-bottom: 1px solid var(--window-color);
}
.navbar .nav>li>.dropdown-menu:after {
    border-bottom: 6px solid var(--window-color);
}
/* submission */

.path-mod-assign td.submissionnotgraded {
    color: var(--text-color);
}
.path-mod-assign td.submissionstatussubmitted {
    color: var(--text-color);
}
table#form td.submit, .form-buttons, .path-admin .buttons, #fitem_id_submitbutton, .fp-content-center form+div, div.backup-section+form, #fgroup_id_buttonar {
    background-color: var(--window-color);
    border-top: 1px solid var(--header-color);
}

/* file */


.path-mod-assign td.submissionlocked {
    background-color: var(--background-color);
    color: var(--text-color);
}
.path-mod-assign td.earlysubmission {
    color: var(--text-color);
    Background-color: rgba(0,0,0,0);
}
.path-mod-assign td.submissionnotgraded {
    background-color: var(--background-color);
}
.feedbacktable .generaltable:not(.user-grade) > tbody>tr:nth-child(odd)>td,
.submissionsummarytable .generaltable:not(.user-grade) > tbody>tr:nth-child(odd)>td {
    background-color: var(--window-color) !important;
}
.filepicker-filelist, .filemanager-container {
    background: var(--window-color);
    border: 1px solid var(--header-color);
}
.dndupload-arrow {
    mix-blend-mode: multiply;
}

/* marks */

.path-my .early {
    background-color: rgba(0, 0, 0, 0);
    color: rgb(46, 204, 113) !important;
}
/* bewertung */

.span9.pull-right {
    background-color: var(--window-color) !important;
    border: none;
    box-shadow: 2px 2px 0 var(--shadow-color) !important;
}
.nav-tabs {
    border-bottom: 1px solid var(--header-color);
}
.nav.nav-tabs+.nav.nav-tabs>.active>a {
    border-bottom: 1px solid var(--header-color) !important;
}
.nav-tabs>.active>a {
    background-color: var(--window-color) !important;
    color: var(--textWhite-color) !important;
    border: 1px solid var(--header-color);
    border-bottom-color: transparent;
}

/* group registration */

.path-mod-grouptool #registration_form .group.empty {
    background-color: var(--window-color);
}
.path-mod-grouptool #registration_form .group {
    border: 1px solid var(--header-color);
}
.form-item .form-description,
.felement.fstatic {
    color: #969696;
}
#id_groups .group span {
    color: rgb(153, 153, 153) !important;
}
.moodle-dialogue-bd .yui3-widget-bd {
    background-color: var(--window-color) !important;
}
.moodle-dialogue-base .moodle-dialogue-wrap {
    background-color: var(--window-color);
    border: 1px solid var(--header-color);
    -webkit-box-shadow: 5px 5px 20px 0 var(--header-color);
    -moz-box-shadow: 5px 5px 20px 0 var(--header-color);
    box-shadow: 5px 5px 20px 0 var(--header-color);
}
.moodle-dialogue-base .moodle-dialogue-wrap .moodle-dialogue-hd,
.moodle-dialogue-base .moodle-dialogue-wrap .moodle-dialogue-hd.yui3-widget-hd {
    text-shadow: none;
    border-bottom: 1px solid var(--header-color);
    color: var(--text-color);
    background-color: var(--background-color);
    background-image: none;
}
legend {
    border-bottom-color: #969696;
}
.moodle-dialogue-base .moodle-dialogue .moodle-dialogue-bd {
    color: var(--text-color);
}
/* forum */

.forumpost {
    background-color: var(--window-color);
    border: none;
    box-shadow: 2px 2px 0 var(--shadow-color) !important;
}
.forumheaderlist > tbody>tr:nth-child(odd)>td, .forumheaderlist > tbody>tr:nth-child(odd):hover{
    background-color: var(--window-color);
}
.forumheaderlist > tbody>tr:nth-child(even):hover>td, .forumheaderlist > tbody>tr:nth-child(even):hover {
    background-color: rgba(0, 0, 0, 0);
}
/*** table ***/
/* main table */
#region-main .leveleven, 
.generaltable tbody tr:nth-child(odd) td,
table.flexible tbody tr:nth-child(odd) td,
.path-mod-assign .gradingtable tr.unselectedrow td {
background-color: var(--background-color);
}
#region-main .levelodd,
#fitem_id_submitbutton{
background-color: var(--window-color);
}
.generaltable tbody tr:hover:nth-child(even) td, table.flexible tbody tr:hover:nth-child(even) td,
.generaltable tbody tr:hover>th{
 background-color: rgba(0,0,0,0);   
}
.generaltable tbody tr:hover:nth-child(even){
 background-color: rgba(0, 0, 0, 0);   
}

/*.generaltable tbody tr:hover:nth-child(odd) td,  table.flexible tbody tr:hover:nth-child(odd) td*/

table.flexible th, .generaltable th, table.flexible td, .generaltable td, #fitem_id_submitbutton {
   border-top: 1px solid var(--header-color);
}
.path-local-checkmarkreport #checkmarkreporttable table, .path-local-checkmarkreport #checkmarkreporttable table th, .path-local-checkmarkreport #checkmarkreporttable table td {
    border: 1px solid var(--header-color);
}

/* Submission color */
#region-main td.submissionstatussubmitted { color: black; }
#region-main .submissionsummarytable tr:hover td.submissionstatussubmitted {
    background-color: #cfefcf;
    color: black;
}

/* quiz */

#page-mod-quiz-view table.quizattemptsummary tr.bestrow td {
    border: none;
    background-color: #75a7bf;;
}

/* */
.table-striped tbody>tr:nth-child(odd)>td, .table-striped tbody>tr:nth-child(odd)>th,
.table-striped tbody>tr:hover:nth-child(odd)>td, .table-striped tbody>tr:hover:nth-child(odd)>th,
.path-local-checkmarkreport #checkmarkreporttable table .r1,
.table-hover tbody tr:hover>td, .table-hover tbody tr:hover>th {
    background-color: var(--window-color);
}

/* tab */
.nav-tabs>li>a:hover, .nav-tabs>li>a:focus {
    border-color: var(--header-color) var(--header-color) var(--header-color);
    border-bottom-color: transparent;
}
.nav-tabs>.active>a:hover {
    border: 1px solid var(--header-color);
    border-bottom-color: transparent;
}
.nav>li>a:hover, .nav>li>a:focus {
    background-color: var(--background-color);
}

/* button */

.breadcrumb {
    background-color: var(--window-color) !important;
    border: none;
    box-shadow: 2px 2px 0 var(--shadow-color) !important;
}
.breadcrumb>li {
    text-shadow: none;
}
/* middle column lva selection */

.course-content > ul > li {
    background-color: var(--window-color) !important;
    box-shadow: 2px 2px 0 var(--shadow-color) !important;
}
/*
.block {
    background-color: var(--window-color) !important;
    box-shadow: 2px 2px 0 var(--shadow-color) !important;
    border: none;
}*/
.block .header h2 {
    text-shadow: none !important;
}
.block_semester_sortierung fieldset {
    border: 1px solid var(--shadow-color);
}
.block_semester_sortierung .semester>legend {
    color: var(--text-color);
}
.block_semester_sortierung .course>legend>a {
    color: #75a7bf;
}
.block_semester_sortierung fieldset fieldset/*,
.block_rss_client .content li*/ {
    border: 1px solid var(--shadow-color);
}
.block_rss_client .list li {
    border-top: 1px solid var(--background-color);
    
}
hr {
    border-top: 1px solid var(--window-color);
    border-bottom: 1px solid var(--shadow-color);
}
/* middle column lva */

.topics > li {
    box-shadow: 2px 2px 0 var(--shadow-color) !important;
}
.course-content ul li.section.main {
    border: none !important;
    ;
    margin-bottom: 10px !important;
    ;
    padding-top: 10px !important;
    ;
}
/* text */

.block .header h2 {
    color: var(--text-color);
}

a:link,
a:visited {
    color: #75a7bf !important;
}
.dimmed_text {
    color: rgb(163, 167, 181) !important;
}
.block_semester_sortierung .course>legend>a:hover {
    color: #6C7A89;
}
/* footer */

#page-footer a {
    color: #75a7bf;
}
/* register */
/* top */

.registered_slot > td {
    color: var(--background-color);
}

.registered_slot > td > a {
    color: var(--shadow-color) !important;
    background-color: rgba(0, 0, 0, 0) !important;
}
/* below */
.list-group-item {
	background-color: var(--window-color) !important;
    border: 1px solid var(--shadow-color);
}

.dropdown-divider{
	background-color: var(--shadow-color) !important;
    
}

#nav-drawer{
    background-color: var(--background-color) !important; 
    border: 1px solid var(--shadow-color);
}
/*
.container-fluid div[role=main]{
    border: 1px solid var(--shadow-color);
}*/

.page-footer, #page-footer{
    background-color: var(--background-color) !important;
    border: 1px solid var(--background-color);
}

.card, .card-block {
    background-color: var(--background-color) !important;
    border: 1px solid var(--background-color);
}

#slot_overview .registered_slot span {
    background-color: rgba(0, 0, 0, 0) !important;
}
#slot_overview .registered_slot em {
    color: var(--shadow-color) !important;
}
#slot_overview .registered_slot > td > a {
    background-color: rgba(0, 0, 0, 0) !important;
}
#slot_overview tr:nth-child(odd) > td {
    background-color: var(--window-color) !important;
}

/* code */

code {
    background-color: var(--window-color);
    border: 1px solid var(--header-color);
}

/* messages */
.message .messagearea .messagehistory .notification {
    background-color: var(--window-color);
    border-color: var(--header-color);
}
}

