css:
@namespace url(http://www.w3.org/1999/xhtml); 
@-moz-document url("http://mudcat.org/"),
               url-prefix("http://mudcat.org/?"),
               url-prefix("http://mudcat.org/index.cfm"),
               url-prefix("http://mudcat.org/threads.cfm"),
               url("https://mudcat.org/"),
               url-prefix("https://mudcat.org/?"),
               url-prefix("https://mudcat.org/index.cfm"),
               url-prefix("https://mudcat.org/threads.cfm")
/* Added match URLs 21-8-2017 */
/* mudcat - reorder columns on index for small screens */
/* Version 2 13-10-2017 */
/* Include TLS pages */
{
/* Move the messages column to the extreme left */  
body > table:nth-child(3) td:nth-child(2) {position:absolute ! important;
                       left : 5px ! important; }
/* Move the subject column to the right */ 
body > table:nth-child(3) td:nth-child(1) {position:relative ! important;
                       left : +80px ! important; }

/* Add ^^ to the small d to make it easier to tap */
body > table:nth-child(3) td:nth-child(2) a:nth-child(2)::after { 
                       content:"^^"  !important;
                       color : blue !important ;
                       font-size: 12px !important ;
                       position: relative !important;                          
                       }
}
