css:
@-moz-document domain(zhihu.com) {
.feed-item:hover,.zm-item-answer:hover {
    box-shadow: 2px 2px 2px #bfbfbf; 
    z-index: 1000;
    background: #f5f5f5;
    left: 0;
    border-left: 5px solid #4caf50;
}
.feed-item,.zm-item-answer {
    padding: 10px !important;
    border-left: 5px solid transparent;
    margin: 10px 0;
    transition: 0.4s;
    border-radius: 3px;
    box-shadow: 0px 1px 1px 0px #bfbfbf;
}
.zg-wrap {
    width: auto !important;
    min-width: 960px;
    margin: 0 auto;
    clear: both;
    max-width: 1200px;
}
.zh-footer {
    overflow: hidden
}
.zm-item-answer{
	margin-left:-58px;
}
.zm-votebar{
	margin-left:-58px;
}
}
