css:
@-moz-document domain("vk.com") {
#left_ads,
#ads_left{
	display: none !important;
}


[class*='_img']
,[class*='_image']
,[class*='photo']
,[class*='ava']
,.thumb
,.ow_ava.ow_ava_comm
,img{
	border-radius: 0px !important;
}

.post_full_like_wrap .post_like
,.post_full_like_wrap .post_share {
    float: right;
}

[data-ad-view]{
    display:none;
}

#feed_rmenu, #feed_filters, #public_links, #public_albums, #public_videos, #public_audios, #chat_onl_wrap{
	display:none !important;
}

.wide_column_left .narrow_column_wrap {
	right: calc(1%);
    opacity: 0.1;
}

.wide_column_left .narrow_column_wrap:hover{
	opacity: 1;
}

/*normilize width*/
#page_layout{
 	width: 100% !important;   
}


#page_header{
	width: 100% !important;
}

body{
    overflow-x: hidden !important;
}

#page_body{
	width: 85% !important;
   /* overflow-x: hidden;*/
}

#footer_wrap{
	width: 100% !important;
    padding: 20px !important;
}

.wide_column{
	width: 97% !important;
    padding-right: 3%;
}


/*dev page*/
.dev #page_body{
	width: 75%;
}

.dev #dev_page{
    width: 75%;
}

.dev:not(.main) #dev_footer_wrap{
	transform: translate(-20%,0%);
}

.dev.main #dev_page_wrap2{
	width: 100%;
}

#dev_page_wrap2{
	width: 75%;
    box-sizing: border-box;
    padding-right: 100px;
}

#dev_page{
    margin-left: 165px;
}

/*
.dev_page_block.dev_main_sections{
	position: relative;
    right: 250px;
}
*/

/*normilize dialogs*/
#im--page{
	width: 85%;
}

#im--page+div{
	margin-left: 0px;
    right: 23px;
    width: 150px;
	z-index: 103;
}

.im-page--header-chat,
.im-page--chat-input{
	width: 50% !important;
}

.im-mess-stack{
	max-width: 100%;
}




/*align news*/
.page_post_sized_thumbs{
    display:inline-block;
}

._wall_post_cont{
	text-align: center;
}

.wall_post_text{
	text-align: left;
}

/*
.replies{
	opacity: 0.2;
    transition: all 3s;
}

.replies:hover{
	opacity: 1;
}
*/

/*
#public .replies_list a.wr_header:not(.wrh_all)~div{
*/

.replies_list a.wr_header:not(.wrh_all)~div{
	display: none;
}

.replies_list input:first-child[type='hidden']~div{
	opacity: 0.1;
    transition: all 3s;
}

.replies_list input:first-child[type='hidden']~div:hover{
    opacity: 1;
}

.post_share{
	opacity: 0.1;
    transition: all 3s;
}

.post_share:hover{
	opacity:1;
}

.wr_header,
.reply_link_wrap,
.wall_signed,
#l_vid,
#l_ph,
#l_gr,
#l_fr,
#l_fav,
#l_doc,
#l_apm
{
	opacity: 0.1 !important;
}

.wr_header:hover,
.reply_link_wrap:hover,
.wall_signed:hover,
#l_vid:hover,
#l_ph:hover,
#l_gr:hover,
#l_fr:hover,
#l_fav:hover,
#l_doc:hover,
#l_apm:hover
{
    transition: all 3s;
	opacity: 1 !important;
}

/*position to top text*/
#stl_text{
	left: 115px;
    position: relative;
}


.like_cont {
 flex-direction: row-reverse;
}

}
