@-moz-document domain('artist-grapevine.tumblr.com'), domain('artist-tips.tumblr.com') {
#credit .creditr {
display:none!important
}

::-webkit-scrollbar {
    background: none;
}

html {
background:#28202a!important
}

body {
color:#c7bbbd!important;
background-image:none;
background:none!important
}

.l-content .heading h2 {
    color: #c7bbbd!important;}

.blue_bar {
    background-color: #3d303d;
}

.post_micro.is_conversation .post_content, .post_micro.is_note .post_content, .post_micro.is_quote .post_content, .post_micro.is_regular .post_content {
    background: #3d303d;
}

/*[[background]]*/

p {
color:#c7bbbd!important
}

a {
color:#ae6e93
}

a:hover {
color:#c7bbbd!important;
background:none!important
}

#seokjin {
border:1px solid #0e0e0e!important;
background:#3d303d!important;
/*[[sidebar]]*/
}

#seokjin .profile img {
display:inline-block!important;
-moz-box-sizing:border-box!important;
box-sizing:border-box!important;
background:url(http://i.imgur.com/ZxbT86D.png)#0e0e0e no-repeat!important;
border:1px solid #0e0e0e!important;
opacity:.8!important;
height:120px!important;
width:120px!important;
border-radius:70px!important;
padding-top:120px!important
}

#seokjin .profile:hover img {
opacity:1!important;
transform:none!important;
-ms-transform:none!important;
-webkit-transform:none!important;
-moz-transform:none!important;
/*all hail the kawaii hydra*/
}

.jihoon {
background:#121112!important;
border:1px solid #121112!important;
opacity:.8!important
}

.zelo a {
color:#ae6e93!important;
background:#28202a!important;
border:1px solid #0e0e0e!important
}

.zelo a:hover {
color:#c7bbbd!important
}

#pagi a {
color:#ae6e93!important;
background:#28202a!important;
border:1px solid #0e0e0e!important
}

#pagi a:hover {
color:#c7bbbd!important
}

span.current_page {
color:#0e0e0e!important
}

#seokjin .desc {
color:#c7bbbd!important;
background:#28202a!important;
border:1px solid #0e0e0e!important;
outline:0 auto #0e0e0e!important
}

.nav a {
color:#ae6e93!important;
background-color:#3d303d!important;
border:1px solid #0e0e0e!important;
}

.nav a:hover {
-webkit-box-shadow:none!important;
-moz-box-shadow:none!important;
box-shadow:none!important;
color:#c7bbbd!important
}

#seokjin div {
background:none!important;
border:none!important;
border-radius:3px;
color:#ae6e93!important
}

.search form {
background:#28202a!important;
border:1px solid #0e0e0e!important;
outline:0 auto #0e0e0e!important;
border-radius:5px!important;
width:calc(100% + 15px)!important;
margin-left:-8px!important
}

.search form .query {
color:#c7bbbd
}

.search form .submit {
color:#ae6e93!important;
transition:.2s ease-in-out!important;
-moz-transition:.2s ease-in-out!important;
-webkit-transition:.2s ease-in-out!important
}

.search form .submit:hover {
color:#c7bbbd!important
}

#youngjae {
color:#c7bbbd!important;
background:#3d303d!important;
border:1px solid #0e0e0e!important;
outline:0 auto #0e0e0e!important
}

#youngjae .title {
color:#c7bbbd!important;
background:#28202a!important;
border:1px solid #0e0e0e!important;
outline:0 auto #0e0e0e!important
}

.question {
color:#c7bbbd!important;
background:#28202a!important;
border:1px solid #0e0e0e!important;
outline:0 auto #0e0e0e!important
}

.qarrowleft {
border-right:11px solid #28202a!important
}

#youngjae img {
opacity:/*[[imgopacity]]*/;
}

#youngjae #jonguppie .tags a {
color:#ae6e93!important;
background:#28202a!important;
border:1px solid #0e0e0e!important
}

#youngjae #jonguppie .tags a:hover {
-webkit-box-shadow:none!important;
-moz-box-shadow:none!important;
box-shadow:none!important;
color:#c7bbbd!important
}

#youngjae #jonguppie #bebop .tago,#youngjae #jonguppie #bebop .tago a {
color:#ae6e93!important
}

#youngjae .texto * {
color: #c7bbbd!important;
background-color: transparent!important;
}

#youngjae .texto a, #youngjae .texto a *{
color: #ae6e93!important;   
}

