@-moz-document regexp('https?://disqus\.com/embed/comments/.*artist-grapevine\.tumblr\.com.*'), regexp('https?://disqus\.com/embed/comments/.*artist-tips\.tumblr\.com.*') {
html,body,#container-page,.site-nav--wrapper,#container-main,.more-wrapper,.aside__wrap,.card-wrap,.drawer-footer,.nav-notifs,.nav-tabs,.discussion-page,.share,.nav,.card-profile-post,.form,.content__wrap,.card__additional {
color:#c7bbbd!important;
background:#3d303d!important;
}

a,.publisher-anchor-color a,.nav .dropdown-toggle,.nav-primary > ul > li.active > a,body.dark .nav > ul > li > a,body.dark .nav > ul > li.active > a,body.dark .nav > ul > li > div > a,a.publisher-anchor-color,.post-content footer .voting .vote-up,.post-content footer .voting .vote-down,a span {
color:#ae6e93!important
}

h1,h2,h3,h4,h5,h6,strong,a .notification-count,.post-actions .cancel:hover,.card-comment .comment-message,.icon-flag,.collapse span,.expand span,.textarea-wrapper .placeholder,.text-gray,.profile-cover-list li,.card-reason,.card-summary-content,.channel-follow__description,.channel-stat__subtext,.recommended__reason {
color:#c7bbbd!important
}

.nav-primary > ul > li.notification-menu.unread a.notification-container .notification-icon {
color:#F55567!important
}

.post-content footer .voting .vote-up.upvoted .control,.post-content footer .voting .vote-up.upvoted .count {
color:green!important
}

.post-content footer .voting .vote-down.downvoted .control {
color:red!important
}

#AdSense3,.discovery-main,#footer {
display:none!important
}

.dropdown-menu a:hover,.button-fill,.button-fill--brand {
color:#c7bbbd!important;
background:#ae6e93!important
}

.site-nav--wrapper {
color:#c7bbbd!important;
background:#0e0e0e!important
}

.nav__item,.nav--no-aside,.nav__spacing,.dropdown-menu {
color:#c7bbbd!important;
background:#0e0e0e!important;
border:none!important
}

.btn,.load-more,.cover {
color:#c7bbbd!important;
background:#0e0e0e!important;
margin:0!important;
box-shadow:none!important
}

.post-actions .cancel {
color:#c7bbbd!important;
background:#0e0e0e!important;
margin:0 5px 0 0 !important;
padding:4px 10px!important;
line-height:30px!important;
box-shadow:none!important;
font-weight:700!important
}

.textarea-wrapper .textarea,.postbox .media-preview {
color:#c7bbbd!important;
background:#28202a!important;
border:none!important;
border-radius:0!important
}

.post-actions,.card-profile-forum,.alert--brand {
background:#28202a!important;
border:none!important
}

.post-actions .wysiwyg li a.attach span {
background-image:url(http://img.photobucket.com/albums/v24/novadestin/sprite.png)!important
}

.active .publisher-nav-color::after {
background:#ae6e93!important
}

.dropdown-menu,.textarea-wrapper {
border:1px solid #0e0e0e!important
}

.cover {
border-bottom:1px solid #c7bbbd!important
}

.cover-nav-wrapper {
border-top:none!important
}

.cover-body {
margin-bottom:-15px!important
}

.avatar .user {
opacity:.8
}

.input--text {
color:#c7bbbd!important;
background:#28202a!important;
border:1px solid #0e0e0e!important
}

.acceptance-wrapper {
color:#c7bbbd!important
}

.highlighted-post .highlighted,.highlighted>.post-content,.alert {
background-color:#28202a;
color:#c7bbbd;
border-radius:5px
}

.tooltip {
background:#28202a;
color:#c7bbbd
}

.tooltip__footer {
background:#28202a
}

.story-ad {
background:none!important;
}

.alert.alert--realtime, .alert.info, .alert.success, .dark .alert.alert--realtime, .dark .alert.info, .dark .alert.success {
background: #ae6e93;
}

.textarea-wrapper {
background: #0e0e0e;
}
}

@-moz-document url('https://www.tumblr.com/ask_form/artist-grapevine.tumblr.com'), url('https://www.tumblr.com/submit_form/artist-grapevine.tumblr.com'), url('https://www.tumblr.com/ask_form/artist-tips.tumblr.com'), url('https://www.tumblr.com/submit_form/artist-tips.tumblr.com') {#error_old_ie,#error_no_javascript,.l-container {
background:#28202a!important;
border:1px solid #0e0e0e!important;
border-radius:3px;
padding:10px;
height:100%;
min-height:168px;
box-sizing:border-box
}

#question,#answer {
background:#28202a;
color:#c7bbbd
}

.footer {
border-color:#ae6e93
}

.footer:before,.footer:after {
border:6px solid #ae6e93;
border-left:6px solid transparent;
border-right:6px solid transparent;
border-bottom:none
}

.header {
border-bottom:1px solid #3d303d;
color: #ae6e93;
}

#post_select {
border-right:1px solid #3d303d
}

#post_select.multi:hover,#post_select.multi.active {
background:#fafafa
}

.bluthSkin table.mceLayout tr.mceFirst td {
border-top:1px solid #171417
}

.bluthSkin td.mceToolbar {
background:#3d303d;
padding-top:1px;
vertical-align:top
}

.bluthSkin .mceIcon {
background:transparent url(http://i.imgur.com/yA3YiN0.png) 0 0 no-repeat;
cursor:pointer!important
}

#tags {
    border-top: 1px solid #ae6e93;}

.bluthSkin .mceIframeContainer {
    border-top: 1px dashed #c7bbbd;}

input, textarea, #post_select .txt{
color:#c7bbbd;}

#post_select.multi:hover, #post_select.multi.active{
color:#c7bbbd!important;
background:#3d303d!important
}

.mceContentBody a{
color:#ae6e93
}

body#tinymce.mceContentBody {
color:#c7bbbd!important;
background:#3d303d!important
}

}
