@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("learn2.open.ac.uk"), domain("learn3.open.ac.uk"){
/* email subscription, atom, rss section */

.forumng_header_info {
    display: none;
}
/* Forum tabs */

.osep-activitynav {
    display: none;
}
/* Big forum title */

.forumng_header {
    display: none;
}
