    padding-right: 1px;
}
table.flexible th:first-child,
.generaltable th:first-child,
table.flexible td:first-child,
.generaltable td:first-child {
    padding-left: 1px;
}
/* General page white space */

#osep-pagewrapper,
.osep-layoutright #osep-pagewrapper {
    margin-left: 1px;
    margin-right: 1px;
}
