 /* Main Page */

  
h2.subhead{
   color: black !important;
  font-weight: 600 !important;}
  /* THIS NEEDS TO BE MORE SPECIFIC FOR OTHER CASES
   .text-container p{
      
     font-weight: 300 !important;
    color: red!important;
      font-size: 130% !important;
    }  
   */
    .breadcrumbs {
      color: black!important;
    }
    .breadcrumbs > ul:last-child > li:last-child a{
      color: black !important;
      font-size: 1em !important;
     font-style: italic;
      font-weight: 600 !important;
      
    }
     
  
    time, .parbase.intro.introtext.text  p{
      color: black !important;
    }
    
    .section-text.parbase.text.textsection h3 + div.text-container p{
      font-size: 130% !important;
