      color: black!important; 
      line-height: normal !important;
      
    }
      .imageandtext.parbase.section h4 + .text-container p{
      font-size: 130% !important;
      font-weight: 300 !important;
      color: black!important;     
    }

  
    .text-container>ul li {font-size: 120% !important;
  
  font-weight: 300 !important;
    color: black !important;}

    h1.overview-title{
      color: black !important;
     font-weight: normal !important; 
    } 
    
    ul a {font-size:18px !important;
      font-weight: 400 !important;
    }
    
    .parbase.intro.introtext.text  p{
      color: black !important;
    }
    
    
    .toutItem.toutLinkBlock span {
       font-weight: 400 !important;
       color: black !important;
    }
      
    .section.producttextlists .column-title a{
    color: black !important;  
    }
      .column-title+div a, .avoid-break~div a {
    color: black !important;  
    }
     /*
 Removed 6/15/18, Consumers improved web page. Not needed now.

	.parbase.section.text p {
        
        font-weight: 400 !important;
       color: black !important;
        font-size:120% !important
      }

    /*Updates 5/31/17 */
    
    
    .recommendations-header-container h2,
    .ratings-tabs-component-instance li
  {
      font-family:"Averta W01 Regular" !important;
      opacity: 1 !important;
      color: black !important;
    }   
    .recommended-row-container .bottom-line{
      font-size:0.9em!important;
       }
    
    .gnav-breadcrumbs > ul:last-child > li:last-child a{
      color: black !important;  
     font-style: italic;
      font-weight: 300 !important;
    } 
  
  .btn-compare__add{
      font-size:130% !important;
      background-size: 13%!important;   
      color: black !important;
    }
    
    .overview-link a{
      font-size:1em !important;
    }
    .recommendations-header-container p p span{
      font-size:100%!important;
    }  
  
  .product-object.brand_model_price_column.brand_model_price{
    padding-left: 2px!important;/*"Add to compare" was made larger so need to pad this column to avoid interference*/
  }
    
  /*Product reviews - Overview*/

 div.paragraph,  .news-content {
    
    color: black !important;
  font-size: 120% !important
  }

  div.ratings-recommended-content{
    
  font-size: 120%  !important; 
  } 
    
