  #ipsLayout_header header { background-position-x: 50%; background-color: #111; background-image: url('http://puu.sh/s3W90/c68f7a9d1a.png'); } /* шапка */
  
  /*Цвет заголовка страницы*/
  .ipsType_pageTitle {color: #909090!important;}
  
  /*Основной фон*/
  #ipsLayout_contentWrapper {background-color: #111!important;}
  
  /*Надразделы*/
  .ipsAreaBackground_reset {background-color: #333!important;} /*полоса 1*/
  
  .ipsDataList.ipsDataList_zebra .ipsDataItem:nth-child(2n):not(.ipsDataItem_selected):not(.ipsModerated):not(.ipsDataItem_new):not(.ipsDataItem_success):not(.ipsDataItem_warning):not(.ipsDataItem_error):not(.ipsDataItem_info):not(.ipsDataItem_status) {background: #232323!important;} /*полоса 2*/
  
  .ipsBox:not(.ipsBox_transparent):not(.ipsModerated) {background-color: #222!important;} /*фон правых панелей и постов в темах*/
  
  h2.ipsType_sectionTitle {background: linear-gradient(to bottom, #5A3418 0%,#6e442c 100%)!important;} /*цвет фона заголовка*/
  
  a.ipsType_sectionTitle, .ipsType_sectionTitle a, .ipsType_sectionTitle {color: #aaa!important;} /*цвет заголовков надразделов и надписи "Разделы"*/
  
  
  
  /* Панель профиля */
  #elUserNav > li > a { color: #bbb!important; }
  
  /* Панель меню у профиля */
  .ipsMenu_title { background-color: #1c1c1c; }
  ul:not(.ipsMenu_keyNav) .ipsMenu_item:not(.ipsMenu_itemClicked):not(.ipsMenu_itemDisabled) a:hover, .ipsMenu_item[data-selected] a, .ipsMenu_item[data-selected] span, .ipsMenu_item.ipsMenu_hover { background-color: #191919; }
  .ipsMenu_sep { border-color: #333; }
  
  
  
  
  /*Фоны верхнего меню*/
  .ipsNavBar_secondary > li > a:hover { border-color: #111; color: #bbb!important; }
  .ipsNavBar_secondary > li.ipsNavBar_active a { color: #909090!important; border-color: #111; }
  
  .ipsNavBar_primary > ul > li.ipsNavBar_active > a {background-color: #333!important;} /*"Сообщества"...*/
  
  .ipsNavBar_secondary {background-color: #333!important;} /*панель-меню*/
  
  .ipsNavBar_primary:not(.ipsNavBar_noSubBars) > ul::before {background-color: #333!important;} /*пустая панель-меню у Техподдержки*/
  
  
  /*Панель внизу, фон*/
  .ipsWidget.ipsWidget_horizontal .ipsWidget_title {background-color: #303030!important;}
  
  /*Полоса под никами*/
  .ipsPad_half.ipsWidget_inner {border-bottom-color: #333!important;}
  
  /*Лучшие авторы*/
  .ipsTabs_panel {background-color: #333!important;} /*фон*/
  
  .ipsDataItem {border-color: #222!important;} /*разделяющие полосы*/
  
  .ipsTabs_activeItem {background-color: #333!important;} /*фон активного меню*/
  
  
  /*Правые модули*/
