  .ipsAreaBackground {background-color: #181818!important;} /*фон окна изменения статуса*/
  
  .ipsComposeArea_editor {background-color: #282828!important;} /*граница вокруг поля ввода*/
  
  .ipsComposeArea_dummy {background-color: #303030!important;} /*фон псевдо-поля ввода*/
  
  .ipsApp .ipsButton_primary {color: #aaa!important;} /*текст кнопки "обновить статус"*/
  
  
  /*фон иконки непрочитанных форумов*/
