  .ipsItemStatus.ipsItemStatus_large.ipsItemStatus_read {background-color: #6e442c!important; color: #222!important;}
  .ipsItemStatus.ipsItemStatus_read {opacity: 0.3!important;}
  
  /*фон авок*/
