  /*.ipsUserPhoto_tiny img, img.ipsUserPhoto_tiny, .ipsUserPhoto_tiny::after {opacity: 0.8!important;} /*затемнение авок*/
  
  /*#elLogo img {opacity: 0.8!important;} /*затемнение лого*/
  
  
  
  
