  /*Панель со страницами*/
  .ipsButtonBar {background-color: #6b4029!important;} /*фон панели со страницами*/
  
  .ipsDataItem {background-color: #202020!important;} /*фон 1 полосы названий разделов и тем (второй в надразделах выше "полоса 2")*/
  
