  {background-color: RGBA(51, 51, 51, 0.4)!important; color: #909090; } /*фон номеров страниц*/
  
  .ipsButtonBar .ipsPagination .ipsPagination_page.ipsPagination_active a {background-color: #333c!important; color: #909090} /*фон номера текущей страницы*/
  
  .ipsButtonBar .ipsButtonRow li > a, .ipsButtonBar .ipsButtonRow li > button, .ipsButtonBar .ipsButtonRow li > span { color: #909090; } /* "сортировка" */
  
  
  
  
  /*Цвет доп текста ("Автор", дата и т.д.)*/
