  .ipsBadge_positive, .ipsBadge_style4 {background-color: #3d633e!important;} /*зелёный фон*/
  
  
  /*Значки тем и подразделов*/
  .ipsItemStatus.ipsItemStatus_read:not(.ipsItemStatus_large) {color: #000!important;} /*звёздочка, прочитано*/
  .ipsItemStatus:not(.ipsItemStatus_large) {color: #6b4029!important;} /*звёздочка и точка, не прочтано*/
  
  
  
  
  /*****  Комментарии  *****/
  
  /*Фон фона коментов*/
  .ipsAreaBackground_light {background-color: #181818!important;}
  
  /* фон названия темы при наведении */
  .ipsType_pageTitle [data-role="editableTitle"]:hover, .ipsType_pageTitle[data-role="editableTitle"]:hover { background: none; }
  
  /*Лучший комент*/
  .ipsComment.ipsComment_popular .ipsComment_popularFlag {color: #111!important; background-color: #1c5359!important;} /*сердечко и фон сердечка*/
  
  .ipsComment_popularFlag::after {border-top-color: #1c5359!important;} /*стрелочка вниз*/
  
  .ipsComment.ipsBox.ipsComment_popular:not(.ipsModerated):not(.ipsComment_selected) {border-color: #1c5359!important;} /*границы вокруг комента*/
  
  
  /*Ранг и город*/
  body {color: #6f6f6f!important;}
  
  /*Плавный переход цвета "жалоба" и поделиться*/
