  a.ipsType_blendLinks:hover:not(.ipsType_noUnderline), .ipsType_blendLinks:not(.ipsType_noUnderline) a:hover:not(.ipsType_noUnderline) { color: #aaa!important; } /* цвет ников при наведении */
  
