  .ipsComment_highlighted:not(.ipsBox_transparent):not(.ipsModerated):not(.ipsComment_selected) { border: 0px !important; box-shadow: 0 0 10px #03c10b; }

  /*Оранжевый фон сообщения адаптирован для тёмной темы*/
  form > .gaijin-important-post + article { background: none repeat scroll 0% 0% rgba(90, 44, 0, 0.6) !important; box-shadow: 0 0 10px #cf5200 !important; }
  
  /* лайки */
  .ipsLikeRep { background-color: #333; }
  .ipsButton.ipsButton_repUp, .ipsButton.ipsButton_repDown { color: #222!important; }
  .ipsButton.ipsButton_repUp { background-color: #518425; }
  .ipsReputation_count, .ipsReputation_count.ipsType_positive { background-color: #2c2c2c; }
  .ipsType_neutral { color: #555; }
  .ipsType_success, .ipsType_positive { color: #518425; }
  
  /* всплывающе окно с инфой профиля */
  .ipsHovercard { background-color: #222; border-color: #333; }
