  .ipsToggle span::after { color: #222; }
  .ipsToggle span { background-color: #222; }
  .ipsToggle span::before { color: #555; }
  .ipsToggle.ipsToggle_off { background-color: #555; }
  
  /* полоса новых сообщений */
  .ipsCommentUnreadSeperator { border-width: 4px 0 0 0; border-color: #743E17; }
  
  /* преобразованная ссылка на тему */
  .ipsEmbedded_headerArea { background: #333; }
  .ipsEmbedded_content { background: #222; }
  iframe[data-embedContent] { background: #1c1c1c; }
  
  /* заметки админов */
  .post-note p { color: #518425; } /* цвет даты добавления */
  .post-note { border-color: #483B0D } /* цвет границы */
  .post-note.pn-type-1 { background-color: #3F3131; border-color: #8F4242; } /* предупреждение */
  .post-note.pn-type-2 { border-color: #146814; background-color: #253025; } /* информация */
  .post-note.pn-type-3 { background-color: #2D2B24; } /* заметка */
  
  
