  /* красные ники админов внизу */
  span[style="font-weight:bold;color:red"] { color: #d10000!important; }
  
  /* код */
  .ipsCode { background: #161616; border-left-color: #111!important; }
  .tag { color: #0270ff; }
  .atn { color: #b700b7; }
  .atv { color: #00ae00; }
  .pln { color: #ccc; }
  .lit { color: #00bcbc; }
  
  /* Заметка над полем ввода */
