  .ipsModerated { background: transparent; color: #909090; }
  
  
  
  /*****  Редактор  *****/
  
  /*.cke_button__bold_icon, .cke_button__italic_icon, .cke_button__underline_icon, .cke_button__ipsemoticon_icon, .cke_button__ipslink_icon {background-image: url("http://puu.sh/pSBWO/4607b36bf0.png")!important;}*/
  [class*="cke_button_icon"] { background-image: url("http://puu.sh/pSBWO/4607b36bf0.png")!important; filter: brightness(0.7); } /*иконки редактора*/
    
  .cke_top {background-color: #282828!important;} /*фон редактора текста*/
    
  a.cke_button_off:hover, a.cke_button_off:focus, a.cke_button_off:active, a.cke_button_disabled:hover, a.cke_button_disabled:focus, a.cke_button_disabled:active
  {background-color: #444!important;} /*фон кнопки при наведении*/
  
  a.cke_button_on {background-color: #383838!important;} /*фон кнопки, когда активирована*/
  
  html[dir="ltr"] .ipsComposeArea_withPhoto .ipsComposeArea_editor::before { border-right-color: #282828; } /* стрелочка на мою аву */
  
  a.cke_combo_button { background-color: #333!important; border-color: #333!important; box-shadow: none!important; background-image: none!important; } /* шрифт и размер */
  
  .cke_combo_off a.cke_combo_button:hover, .cke_combo_off a.cke_combo_button:focus { background-color: #3c3c3c; background-image: none; }
  .cke_combo_text { color: #909090; text-shadow: none; }
  
  .cke_wysiwyg_frame, .cke_wysiwyg_div {background-color: #222!important;} /*фон поля ввода*/
  
  /*исправляет скопированный чёрный текст*/
  div.cPost_contentWrap.ipsPad > div.ipsType_normal.ipsType_richText.ipsContained > p > span > span,
  .ipsType_richText { color: #909090!important; }
  
  /*исправляет установленный черынй текст*/
  .cPost_contentWrap > div:nth-child(1) > div:nth-child(n){
    color: #909090 !important;        
  }
  
  /*черный текст под спойлером и в сообщении "замочная скважина"*/
  .ipsSpoiler_contents > p > b > span,
  .ipsPad .cPost_contentWrap >div:nth-child(1) > p > b > span { color: #909090 !important; }
  
  
  /* обводка цитат и скрытого текста при наведении */ 
  .ipsApp .cke_widget_wrapper:hover > .cke_widget_element, .ipsApp .cke_widget_wrapper:hover .cke_widget_editable { outline: none; }
  .cke_widget_wrapper.cke_widget_focused > .cke_widget_element, .cke_widget_wrapper .cke_widget_editable.cke_widget_editable_focused { outline: none; }
  
  /* фикс расстояния между смайлами */
  .ipsEmoticons_item {  max-width: 60px;}
  
  /* убираю сужение редактора */
  div.cke_wysiwyg_div.cke_reset.cke_enable_context_menu.cke_editable.cke_editable_themed.cke_contents_ltr { max-height: none!important; }
  
  
  
  
  
  /**** Всплывающие менюшки ****/
  .ipsMenu { background-color: #111; border-color: #333; }
  .ipsMenu_headerBar, .ipsMenu_footerBar { background: #333; border-color: #282828; }
  .ipsMenu.ipsMenu_bottomCenter::before, .ipsMenu.ipsMenu_bottomCenter::after { border-bottom-color: #333; }
  .ipsType_sectionHead { color: #909090; }
  .ipsMenu.ipsMenu_bottomRight::before, .ipsMenu.ipsMenu_bottomRight::after { border-bottom-color: #333; }
  
  
  
  /*****  Почта  *****/
