  /* чек боксы */
  .ipsCustomInput > span { background-color: #222; border-color: #333; } /* чек боксы */
  .ipsCustomInput > span:focus { border-color: rgba(36, 75, 107, 0.8); box-shadow: 0 1px 1px rgba(0,0,0,0.075), 0px 1px 5px rgba(44, 84, 117, 0.6) inset; }
  
  /* фон сообщений */
  .ipsComment, .ipsComment_highlighted:not(.ipsBox_transparent):not(.ipsModerated):not(.ipsComment_selected) { background: #222; }
  .ipsComment:not(.ipsModerated) .ipsComment_header { background: #2b2b2b; }
  
  /* страницы */
  .ipsPagination:not(.ipsPagination_mini) .ipsPagination_page a, .ipsPagination:not(.ipsPagination_mini) .ipsPagination_next a, .ipsPagination:not(.ipsPagination_mini) .ipsPagination_prev a, .ipsPagination:not(.ipsPagination_mini) .ipsPagination_first a, .ipsPagination:not(.ipsPagination_mini) .ipsPagination_last a
