  .ipsStream .ipsStreamItem_contentType { background-color: #6e442c; border-color: #222; } /* фон значка форума в правом нижнем углу от авы в активности */
  i.fa.fa-comment { color: #222!important; }
  
  .ipsPageHead_special, #elProfileStats { color: #bbb; } /* цвет ника */
  
  .ipsLeaderboard_trophy { color: #222; } /* трофеи */
  .ipsLeaderboard_trophy_1, .ipsLeaderboard_trophy_1.ipsLeaderboard_trophy::before, .ipsLeaderboard_trophy_1.ipsLeaderboard_trophy::after { background-color: #c58e04; }
  .cProfileSidebarBlock.ipsLeaderboard_trophy_1 { background-color: #333; color: #c58e04; }
  
  .ipsTabs { background-color: #6e442c; } /* фон вкладок */
  .ipsTabs_item { background-color: #333c; }
  .ipsTabs_item:hover:not(.ipsTabs_activeItem) { background-color: #303030; }
  
  .ipsSideMenu_item:not(.ipsSideMenu_itemActive) a:hover, a.ipsSideMenu_item:hover:not(.ipsSideMenu_itemActive) /* настройки аккаунта, ховер меню */
  { background-color: #222; }
  
  /* настройки */
  .ipsFieldRow_section { color: #909090; background: #2b2b2b; }
  .ipsApp select { background-color: #2b2b2b; border-color: #333; }
