css:
@-moz-document domain("zh.opensuse.org"), domain("en.opensuse.org") {
.box.single-margin,
.box.navigation,
.box.box-shadow,
.box-header,
td,
.header-tabs li,
.box-footer,
.toc {
    border-color: transparent !important;
}
.catlinks {
    display: block;
    clear: both;
    border-top: none !important;
    border-left: 2px solid #D9E5BF;
    margin-top: 15px;
}
.container_12,
.container_16 {
    width: 1200px;
    ;
    margin: 0 auto;
}
#global-navigation li a {
    padding: 15px 0;
}
#header,
#global-navigation li {
    height: 51px;
}
#header-logo {
    margin-top: 12px;
    margin-right: 15px;
    margin-left: 15px;
}
/* */

#subheader {
    margin-top: 0;
    margin-bottom: 25px;
    min-height: 17px;
    line-height: 1.2em;
}
/* */

#subheader,
#content,
#content .column > div,
.ui-oo-box-shadow,
.box {
    margin-bottom: 20px;
    box-shadow: none;
}
.box-header,
.box-subheader,
.box-footer,
.header-tabs li.selected {
    background: transparent;
}
.sponsor-image img {
    max-width: 100% !important;
}
#content > div:first-child {
    width: 10%;
    overflow: hidden;
    margin-top: 5px;
    margin-left: -10px;
}
#content > div:first-child:hover {
    overflow: visible;
}
#content > div:nth-child(2) {
    width: 85.75%;
    font-size: 14px;
    padding: 10px 5px;
}
#content > div:first-child .navigation {
    background: transparent;
}
.single-margin {
    margin: 0 0 10px;
}
.box-header.header-tabs,
.box-footer.header-tabs {
    padding: 10px 0;
    text-shadow: none;
}
#mw-content-text > p {
    margin: 10px;
}
#mw-content-text table p,
#mw-content-text div p {
    margin: 0 0 10px 10px;
}
#mw-content-text p b {
    border-bottom: 2px dashed #000 !important;
    border-top: 2px dashed #000 !important;
    font-size: 18px;
    font-weight: normal;
    display: inline-block;
    margin: 10px 0;
}
#mw-content-text td > ul:last-child,
#mw-content-text div > ul:last-child {
    margin-bottom: 40px;
}
.single-margin .mw-headline {
    color: #666;
    font-size: 1em;
}
#mw-content-text .box {
    padding: 14px 0;
    margin: 20px 1em !important;
    line-height: 2;
}
.box-header .mw-redirect:before {
    content: ">> ";
}
#mw-content-text .box.single-margin {
    border-top: 1px dashed #ccc !important;
    padding-top: 20px;
    padding-bottom: 0;
    border-radius: 0;
    margin-bottom: 10px !important;
    margin-top: 0px !important;
}
.box-footer:empty {
    display: none;
}
#mw-content-text {
    counter-reset: section;
}
#mw-content-text > h2:before {
    content: counter(section)"- ";
    counter-increment: section;
}
#mw-content-text > h3:before {
    counter-increment: subsection;
    content: counter(section) "." counter(subsection) " ";
}
#mw-content-text > h2 {
    counter-reset: subsection;
    font-size: 20px;
    margin-top: 36px;
    margin-bottom: 22px;
}
#mw-content-text > h3 {
    margin-top: 24px;
    margin-bottom: 17px;
}
.mw-search-pager-bottom {
    margin-left: 20px;
}
.toc {
    margin: 25px 9px 20px 9px;
    padding: 20px 0;
    border: 1px solid #f0f0f0 !important;
    border-radius: 4px;
    line-height: 3.5;
}
.toc ul {
    margin-bottom: 0 !important;
}
#mw-content-text p br:first-child:last-child {
    display: none;
}
p {
    line-height: 1.8;
}
#footer {
    margin-bottom: 20px;
}
}
