css:
@-moz-document domain("wowcamera.info") {
/* FONT */

* {
	font-family: Lato, Lucida Grande, sans-serif !important;
}

.content {
	font-weight: 300;
}

.content,
.pagination li.active span,
.wppa-arrow,
.breadcrumbs .crumb:before {
	font-size: 18px !important;
}

a,
.signature,
strong, dt, .notice {
	font-size: 15px !important;
}

.author,
.profile-joined,
.inner p,
.pagination {
	font-size: 12px !important;
}

/* COLOR */

html, body {
	background-color: hsla(230, 2%, 11%, 1);
	background-image: none;
}

#wrap, .navbar,
.navbar .inner {
	background-color: transparent !important;
}

.bg2,
.polaroid {
    background-color: hsla(230, 2%, 18%, 1) !important;
}

.bg1, .bg3,
.panel,
li.row,
.inputbox,
.inner,
#navigation a,
#tabs .activetab > a, #tabs .activetab > a:hover,
#tabs .tab > a,
#tabs .tab > a:hover,
.wppa-box,
input[type="text"], input[type="password"], input[type="email"], textarea, select {
    background-color: hsla(230, 2%, 18%, 1) !important;
	background-image: none !important;
} 
	
li.row:nth-child(2n+1) {
	background-color: hsla(230, 2%, 20%, 1);
}

blockquote,
.pagination li a,
.button,
select,
.forabg, .forumbg,
li.row:hover,
.dropdown .dropdown-contents,
.attachbox,
#navigation #active-subsection a,
#ucp .panel,
#ucp .panel .inner,
.wppa-popup,
.wppa-mini-box,
.forabg .inner,
.forumbg .inner { 
	background-color: hsla(230, 2%, 22%, 1) !important;
	background-image: none;
}

.dropdown {
	background-color: transparent;
}

.icon-button {
	background-color: rgba(255, 255, 255, 0.84) !important;
}

.reply-icon,
.tools-icon,
.search-adv-icon,
.dropdown-select,
.newpm-icon { 
	background-color: hsla(230, 2%, 22%, 1) !important;
	background-image: none;
}

.reply-icon:hover,
.tools-icon:hover,
.search-adv-icon:hover,
.dropdown-select:hover,
.newpm-icon:hover { 
	background-color: hsla(230, 2%, 22%, 1) !important;
	background-image: none;
	text-shadow: none;
	color: #fabf5f !important;
}

.pagination,
.content,
.panel,
.postprofile strong,
.profile-joined,
.pagination li a,
.button span,
.signature,
p.author,
label,
select,
h3, h2, h1, h4, h5, h6,
p,
.rightside,
dl.icon dt .list-inner,
.topics,
.posts,
.lastpost span,
.views,
.inputbox,
.quickreply-title,
dt, dd,
.poll-title,
.dropdown-select,
dl.file dd,
.mark,
ul.main-nav .current_page_item a, ul.main-nav .current_menu_item a, ul.main-nav li:hover a,
span.wppa-nav-text,
.wppa-arrow,
input[type="text"], input[type="password"], input[type="email"], textarea, select,
.wppa-popup,
.wppa-alt,
.wppa-fulldesc,
.wppa-nav-text,
.wppa-comments,
.breadcrumbs .crumb:before,
.polaroid,
.notice {
	color: #ccc9c2 !important;
}

.dropdown-trigger .dropdown-toggle {
	color: transparent !important;
}

a,
.button,
#navigation a,
#tabs .tab > a {
    color: #d9a552;
}

a:hover,
#tabs .activetab > a, #tabs .activetab > a {
    color: #fabf5f;
}

.pagination li.next a, .pagination li.previous a, .pagination li.page-jump a {
	color: transparent !important;
}

ul.linklist li.responsive-menu a.responsive-menu-link:before {
	border-color: #E3B600;
}

.polaroid {
	border-color: hsla(230, 2%, 22%, 1);
}

ul.navlinks,
blockquote,
.pagination li a,
.button,
.button,
select,
h3,
hr,
.dropdown-select:after,
.search-box .inputbox, .search-box .inputbox:hover, .search-box .inputbox:focus, .search-box .button:hover,
.inputbox,
.dropdown .dropdown-contents,
.wppa-box,
input[type="text"], input[type="password"], input[type="email"], textarea, select,
.wppa-popup,
.wppa-mini-box {
    border-color: hsla(230, 2%, 22%, 1) !important;
}

.dropdown li, .dropdown li li,
.attachbox {
	border-color: hsla(230, 2%, 32%, 1);
}

.postbody {
	border-left: 1px solid hsla(230, 2%, 22%, 1);
}

.leftsided .postprofile,
.attachbox dd {
	border: none;
}

.highslide img {
    border: 1px solid hsla(230, 2%, 11.5%, 1);
}

.highslide:hover img {
    border-color: hsla(230, 2%, 32%, 1);
}
.inputbox:hover {
	border-color: #11A3EA !important;
}

li.row dd {
    border-left-color: hsla(230, 2%, 32%, 1);
}

li.row:hover dd {
    border-left-color: #6d6d6d;
}

li.row,
.forabg,
.forumbg,
li.row.bg2,
.post,
.button {
	border-radius: 3px !important;
}

a.button1, input.button1, input.button3, a.button2, input.button2 {
    color: #bcbcbc;
    background-color: hsla(230, 2%, 22%, 1);
    background-image: none;
}

.content h2, .panel h2 {
	border-bottom-color: hsla(230, 2%, 22%, 1);
}

ul.main-nav .current_page_item a, ul.main-nav .current_menu_item a, ul.main-nav li:hover a {
	border-bottom-color: #ccc9c2;
}

fieldset.polls dl {
	border-top-color: hsla(230, 2%, 22%, 1);
}

.dropdown .pointer-inner,
.panel-container .panel li.row {
    border-color: hsla(230, 2%, 22%, 1) transparent;
}

ul.cplist,
#tabs .activetab > a, #tabs .activetab > a,
#tabs .activetab > a, #tabs .activetab > a:hover {
	border: none;
	box-shadow: none;
}

.leftsided .online .postprofile {
	background: none;
}

.leftsided .online .username:before {
	content: 'â¢';
    color: #00c71c;
    font-size: 21px;
    position: relative;
    top: 2px;
	margin-right: 8px;
}

/* SHADOWS */
.button {
	box-shadow: 0 0 6px hsla(230, 2%, 11.5%, 0.75);
}

.post {
	 box-shadow: 0 8px 24px hsla(230, 2%, 5%, 0.75);
}

.wrap {
	box-shadow: 0 0px 24px #000;
}

.bg1,
.panel,
li.row {
	 box-shadow: 0 8px 24px hsla(230, 2%, 5%, 0.75);
}

.dropdown .dropdown-contents {
    box-shadow: 2px 3px 8px rgba(0, 0, 0, 0.7);
}
#ucp .panel {
	box-shadow: none;
}

ul.main-nav {
	text-shadow: none;
}

/* POSITIONING	*/
.post {
	margin-bottom: 16px;
	padding: 6px 16px;
}

div[style="height: 2px;"] {
	margin-bottom: 8px !important;
}

dl.icon dt, dl.icon dd {
    min-height: 56px;
}

li.row.bg2 {
    margin-bottom: 1px;
}

.list-inner {
	margin-top: 8px;
}

.navbar {
	padding: 3px 0;
}

#topbar+#wrap,
#topbar {
	display: none;
}

.action-bar {
	margin-bottom: 16px;
}

.leftsided .postprofile dd, .leftsided .postprofile dt {
    margin: 8px 12px 0 0;
}

.leftsided div.postbody {
	width: 77%;
}

.postbody {
	padding-left: 16px;
}
.notice {
	padding-top: 1em;
}

.dropdown li {
	padding: 6px 0;
}

.dropdown .dropdown-contents {
	padding: 6px 12px;
}

.dropdown-select {
	padding: 4px 26px 2px 10px;
}

.notice dt {
	margin-bottom: 8px;
}

.dropdown .pointer-inner {
    top: auto;
    bottom: -10px;
    left: -9px;
}

.tools-icon:before, .modtools-icon:before, .search-icon:before, .search-adv-icon:before {
    background-position: -80px 0px;
    height: 16px;
    margin-top: 1px;
    width: 16px;
}

.wppa-mini-box {
    padding: 6px;
}

.nav-footer {
	padding: 8px;
}

li.header dt, li.header dd {
	padding-top: 6px;
	padding-bottom: 0;
}

li.header dt {
	padding-left: 6px;
}

dd.lastpost {
	width: 230px;
}

dd.topics, dd.posts  {
	line-height: 48px;
}

.pagination li a, .pagination li span {
	    padding: 4px 4px 3px 4px !important;
}

.row .pagination {
	margin-top: -6px;
}


/* PNG  */
.pagination li.page-jump a {
	background-position: 0 -18px;
	opacity: 0.6;
}

.pagination li.page-jump a:hover {
	opacity: 1;
}
.pagination li.previous a {
    background-position: -30px -18px;
	opacity: 0.6;
}
.pagination li.previous a:hover {
    opacity: 1;
}
.pagination li.next a {
    background-position: -50px -18px;
	opacity: 0.6;
}
.pagination li.next a:hover {
    opacity: 1;
}
}

@-moz-document domain("wowcamera.info") {
/* FONT */
@font-face {
	font-family: 'Lato';
	font-style: normal;
	font-weight: 300;
	src: 	local('Lato Light'), 
			url(https://apushkarev.github.io/font/Lato-Light.woff2) format('woff2');
}

@font-face {
	font-family: 'Lato';
	font-style: italic;
	font-weight: 300;
	src: 	local('Lato Light Italic'), 
			url(https://apushkarev.github.io/font/Lato-LightItalic.woff2) format('woff2');
}

@font-face {
	font-family: 'Lato';
	font-style: normal;
	font-weight: 400;
	src: 	local('Lato Regular'), 
			url(https://apushkarev.github.io/font/Lato-Regular.woff2) format('woff2');
}

@font-face {
	font-family: 'Lato';
	font-style: italic;
	font-weight: 400;
	src: 	local('Lato Italic'), 
			url(https://apushkarev.github.io/font/Lato-Italic.woff2) format('woff2');
}

@font-face {
	font-family: 'Lato';
	font-style: normal;
	font-weight: 700;
	src: 	local('Lato Bold'), 
			url(https://apushkarev.github.io/font/Lato-Bold.woff2) format('woff2');
}

@font-face {
	font-family: 'Lato';
	font-style: italic;
	font-weight: 700;
	src: 	local('Lato Bold Italic'), 
			url(https://apushkarev.github.io/font/Lato-BoldItalic.woff2) format('woff2');
}

* {
	font-family: Lato !important;
}

}
