css:
@-moz-document domain("opennet.ru") {
:root { --hue-rotate: -45deg; --hue-unrotate: 45deg; --main-background-color: #051721}
html { background-color: var(--main-background-color); }
img { border-color: var(--main-background-color) !important; }
body { filter: hue-rotate(var(--hue-rotate)) invert(1); }
img { filter: hue-rotate(var(--hue-unrotate)) invert(1); }
td#edit_link > .vt_d:nth-child(2), .vt_d2 { filter: contrast(2) saturate(2) brightness(0.9) invert(1) hue-rotate(var(--hue-unrotate)); }
img[src*="opennet2"] { filter: none; }
iframe { filter: invert(1); }
}
