css:
@-moz-document url-prefix("https://asgardia.space/en/forum/") {
.panel-body .row.panel-row {
    border-left: thick solid #1e1e1e;
    border-bottom-color: #212121;
    background: linear-gradient(to bottom right, #2c2c2c, #1e1e1e);
    box-shadow: 4px 4px 17px -4px rgba(0, 0, 0, 0.75);
    -webkit-transition: all 0.3s ease-out;
    -moz-transition: all 0.3s ease-out;
    -o-transition: all 0.3s ease-out;
    transition: all 0.3s ease-out;
    background-position: 100% 0;
    background-size: 200% 200%;
    background-repeat: repeat-x;
}

.panel-body .row.panel-row:hover {
    background-position: 0 0;
}

.panel.panel-default.post .panel-body {
    border-left: thick solid #1e1e1e;
    background: linear-gradient(to bottom right, #2c2c2c, #1e1e1e);
    box-shadow: 4px 4px 17px -4px rgba(0, 0, 0, 0.75);
    -webkit-transition: all 0.3s ease-out;
    -moz-transition: all 0.3s ease-out;
    -o-transition: all 0.3s ease-out;
    transition: all 0.3s ease-out;
    background-position: 100% 0;
    background-size: 200% 200%;
    background-repeat: repeat-x;
}

.panel.panel-default.post .panel-body:hover {
    background-position: 0 0;
}

.forum-layout .btn-link,
.forum-layout .controls-link-wrapper a {
    color: #ccc;
}

.forum-layout {
    background-color: #2c2c2c;
    color: #ccc;
}

.forum-layout .fa {
    color: #8a5216;
}

.panel {
    background-color: #2c2c2c;
    border: none;
    box-shadow: none;
    -webkit-box-shadow: none;
}

.forum-layout .panel-heading,
.forum-layout .breadcrumb {
    color: #ccc;
    background: linear-gradient(to bottom right, #2c2c2c, #1e1e1e)!important;
    box-shadow: 4px 4px 17px -4px rgba(0, 0, 0, 0.75);
    border-color: transparent;
}

.forum-layout .forum-last-post a.forum-name-link,
.forum-layout .panel-body a.forum-name-link {
    color: #ccc;
}

.forum-layout .forum-last-post a,
.forum-layout .panel-body a {
    color: #1f8ae6;
}

.forum-layout .forum-last-post .topic-name div a,
.forum-layout .panel-body .topic-name div a {
    color: #1f8ae6;
}

.forum-layout .forum-last-post .topic-name a,
.forum-layout .panel-body .topic-name a {
    color: #ccc;
}

.form-control {
    color: #ccc;
    background-color: #212121;
    border-color: #212121!important;
    box-shadow: 4px 4px 17px -4px rgba(0, 0, 0, 0.75);
}

.form-control:focus {
    box-shadow: 4px 4px 17px -4px rgba(15, 15, 15, 0.75);
}

.nav-tabs>li.active>a,
.nav-tabs>li.active>a:hover,
.nav-tabs>li.active>a:focus {
    color: #ccc;
    background-color: #2c2c2c;
}

.forum-layout .forum-last-post a,
.forum-layout .panel-body a {
    color: #bb9862;
}

.forum-layout .forum-last-post .topic-name div a,
.forum-layout .panel-body .topic-name div a {
    color: #bb9862;
}

.pagination>li:first-child>a,
.pagination>li:first-child>span {
    background-color: #2c2c2c;
    border-color: #212121;
}

.pagination>.disabled>a,
.pagination>.disabled>span {
    color: #212121;
    background-color: #2c2c2c;
    border-color: #212121;
}

.pagination>.disabled>span:hover,
.pagination>.disabled>span:focus,
.pagination>.disabled>a:hover,
.pagination>.disabled>a:focus {
    background-color: #212121;
    border-color: #212121;
    color: #212121;
}

.pagination>.active>a,
.pagination>.active>span {
    background-color: #212121;
    border-color: #212121;
    color: #ccc;
}

.pagination>.active>a:hover,
.pagination>.active>span:hover,
.pagination>.active>a:focus,
.pagination>.active>span:focus {
    background-color: #212121;
    border-color: #212121;
}

.pagination>li>a,
.pagination>li>span {
    background-color: #2c2c2c;
    border-color: #212121;
    color: #ccc;
}

.pagination>li>a:hover,
.pagination>li>span:hover,
.pagination>li>a:focus,
.pagination>li>span:focus {
    background-color: #212121;
    border-color: #212121;
    color: #ccc;
}

.forum-layout .topic-actions-block a {
    background-color: #212121;
    color: #ccc;
    border-color: #212121;
    box-shadow: 4px 4px 17px -4px rgba(0, 0, 0, 0.75);
}

.forum-layout .profile-sidebar .btn,
.forum-layout .panel-body .btn,
.forum-layout .poll-option-form .btn {
    background-color: #212121;
}

.forum-layout .btn.btn-large.btn-primary {
    color: #ccc;
    border-color: #212121;
    box-shadow: 4px 4px 17px -4px rgba(0, 0, 0, 0.75);
}

.forum-layout .form-actions input[type="submit"].btn-default {
    color: #ccc;
    border-color: #212121;
    box-shadow: 4px 4px 17px -4px rgba(0, 0, 0, 0.75);
}

.nav-tabs>li.active>a,
.nav-tabs>li.active>a:hover,
.nav-tabs>li.active>a:focus {
    color: #ccc;
    background-color: #212121;
    border-color: #212121!important;
    box-shadow: 4px 4px 17px -4px rgba(0, 0, 0, 0.75);
}

.panel-body .row.post-review {
    margin-bottom: 6px;
    border-left: thick solid #1e1e1e;
    border-bottom-color: transparent;
    background: linear-gradient(to bottom right, #2c2c2c, #1e1e1e);
    box-shadow: 4px 4px 17px -4px rgba(0, 0, 0, 0.75);
    -webkit-transition: all 0.3s ease-out;
    -moz-transition: all 0.3s ease-out;
    -o-transition: all 0.3s ease-out;
    transition: all 0.3s ease-out;
    background-position: 100% 0;
    background-size: 200% 200%;
    background-repeat: repeat-x;
}

.panel-body .row.post-review:hover {
    background-position: 0 0;
}

.forum-layout .profile-sidebar .btn,
.forum-layout .panel-body .btn,
.forum-layout .poll-option-form .btn {
    background-color: #212121;
    border-color: #212121;
}

.forum-layout .forum-last-post .post-controls a,
.forum-layout .panel-body .post-controls a {
    color: #ccc;
}

.forum-layout .profile-sidebar .btn:hover,
.forum-layout .panel-body .btn:hover,
.forum-layout .poll-option-form .btn:hover {
    background-color: #212121;
    color: #ccc;
    box-shadow: 4px 4px 17px -4px rgba(0, 0, 0, 0.75)inset;
}

.btn-info:active:hover,
.btn-info.active:hover,
.open>.dropdown-toggle.btn-info:hover,
.btn-info:active:focus,
.btn-info.active:focus,
.open>.dropdown-toggle.btn-info:focus,
.btn-info:active.focus,
.btn-info.active.focus,
.open>.dropdown-toggle.btn-info.focus,
.btn-primary:active:hover,
.btn-primary.active:hover,
.open>.dropdown-toggle.btn-primary:hover,
.btn-primary:active:focus,
.btn-primary.active:focus,
.open>.dropdown-toggle.btn-primary:focus,
.btn-primary:active.focus,
.btn-primary.active.focus,
.open>.dropdown-toggle.btn-primary.focus {
    border-color: #bb9862;
    background-color: #212121;
    color: #ccc;
}

.forum-layout .profile-sidebar .btn,
.forum-layout .panel-body .btn,
.forum-layout .poll-option-form .btn {
    color: #ccc;
}
}
