css:
@-moz-document domain('codepen.io'){

  .footer-right {
      display: none;
  }

  select#collection-choice {
      display: none;
  }

  button#assets-link {
      display: none;
  }

  button#view-details-button {
      display: none;
  }

  button.button.button-dark.mini-button.delete-button.roll-red {
      display: none;
  }

  footer.site-footer.editor-footer {
      width: auto;
      left: auto;
      right: 0;
      padding: 0;
      background: transparent;
      border: none;
  }

  footer .footer-left {
      padding: 0;
      min-height: 0;
  }

  .footer-left button {
      padding: 7px 10px;
      margin-right: 0;
      border-radius: 0;
      opacity: 0.8;
      /*maybe*/
      margin-top: 9px;
  }

  button.console-toggle-button {
      border-right: 1px solid #333;
      border-radius: 10px 0 0 0;
  }

  body.room-editor.editor.state-htmlOn-cssOn-jsOn.layout-left.layout-side.logged-in {
      padding-bottom: 0;
  }

  /*css*/

  a.pen-title-link, .pen-owner-link,.edit-pen-title {
      color: #444;
  }

  a.pen-owner-link {
      /* text-decoration: underline; */
  }

  .button-editor-outline {
      border-color: #BBB;
      background: gainsboro;
      /* border: none; */
      color: #444;
      /* font-weight: bold; */
  }

  .button-editor-outline:hover {
      color: white;
      background: #444;
      border-color: #343436;
  }

  .pen-title-area .icon-codepen-box {
      fill: #444;
  }

  header#main-header {
      box-shadow: none;
      border-color: gainsboro;
      background: whitesmoke;
      /* border: none; */
  }

  .settings-nub {
      position: absolute;
      right: 38px;
      top: 8px;
  }

  .unsaved #save::after, .unsaved #update::after {
      background: transparent;
  }

  .unsaved #save,.unsaved #update {
      border-color: #FBC02D;
  }

  .unsaved #save:hover, .unsaved #update:hover {
      border-color: #dca00f;
  }

}
