#elLogo img {
    max-width: 50%;
}
body,
#ipsLayout_header header {
    font-family: 'Open Sans', sans-serif;
    background: #ebeef2;
}
.general_nav,
.ipsNavBar_secondary,
.ipsNavBar_active {
    background: #ebeef2;
    -webkit-box-shadow: none;
    border: none;
    color: #000;
}
.ipsNavBar_primary > ul > li.ipsNavBar_active > a,
.ipsNavBar_primary > ul > li > a,
.ipsNavBar_primary > ul > li > a:hover,
.ipsNavBar_secondary > li.ipsNavBar_active a,
.ipsNavBar_secondary > li > a,
.ipsNavBar_secondary > li > a:hover {
    color: #000;
    font-family: 'Open Sans', sans-serif;
}
.ipsNavBar_primary > ul > li > a {
	padding: 10px 10px;
}
.ipsType_pageTitle {
    font-family: 'Open Sans', sans-serif;
}
.ipsQuote {
    font-size: 10px;
    border-width: 1px 1px 1px 1px;
    border-color: #ededed #ededed #ededed #ededed;
}
.ipsButtonBar .ipsPagination .ipsPagination_page.ipsPagination_active a {
    color: #ededed;
}
.ipsButtonBar .ipsPagination .ipsPagination_pageJump a {
	color: #5d5d6f;
}
.ipsButtonBar {
	background: #f5f5f5; 
}
.ipsDataItem_statsLarge .ipsDataItem_stats_number{
	font-size:12px;
	color:#9c9c9c;
}
.footer_content,
.footer_end {
    background: #ebeef2;
}
.mind_footer h3 {
    color: #9c9da9;
}
}
