border:0!important;
border-radius:0!important;
box-shadow:0 2px 2px 0 rgba(0,0,0,0.14),0 1px 5px 0 rgba(0,0,0,0.12),0 3px 1px -2px rgba(0,0,0,0.2)!important;
transition:all .35s ease 0
}

.button-refresh:hover {
background-color:#333!important;
transition:all .35s ease 0
}

.button-share-to-team {
background-color:#444!important;
border:0!important;
border-radius:0!important;
box-shadow:0 2px 2px 0 rgba(0,0,0,0.14),0 1px 5px 0 rgba(0,0,0,0.12),0 3px 1px -2px rgba(0,0,0,0.2)!important;
margin-left:2px;
transition:all .35s ease 0
}

.button-share-to-team:hover {
background-color:#333!important;
transition:all .35s ease 0
}

.entry {
background-color:#222;
border:0!important;
border-radius:0!important;
box-shadow:0 2px 2px 0 rgba(0,0,0,0.15),0 1px 5px 0 rgba(0,0,0,0.13),0 3px 1px -2px rgba(0,0,0,0.21);
color:#CCC!important;
margin:2em 1em 0!important;
max-width:100%!important;
padding:1em!important
}

.entry > .content > .metadata {
font-size:.95em
}

.entry > .content > .summary {
font-size:1.15em!important
}

.entry > .content > a {
color:#DDD!important;
font-size:1.7em!important
}

.entry > .visual {
border-radius:0!important;
box-shadow:none;
margin-right:.75em!important
}

.entry.read {
transition:all .8s ease 0
}

.entry.read > .visual {
box-shadow:none
}

.entry.selected {
background-color:#2A2A2A!important;
transition:all .5s ease .25s
}

.entry.unread {
transition:all .5s ease 0
}

.entryBody {
color:#999!important;
color:#DDD;
max-width:100%!important;
padding:0 1em 1em
}

.entryBody > .content {
color:#DDD
}

.entryBody > .content > img {
margin:0 auto
}

.entryBody > div > img {
border:0!important;
border-radius:0!important;
box-shadow:0 2px 2px 0 rgba(0,0,0,0.14),0 1px 5px 0 rgba(0,0,0,0.12),0 3px 1px -2px rgba(0,0,0,0.2);
margin:0 auto!important;
margin-bottom:1em!important;
max-width:100%!important
}

.entryHeader > a {
color:#DDD!important;
margin-top:1rem!important
}

.entryholder {
background-color:#333;
box-shadow:0 2px 2px 0 rgba(0,0,0,0.14),0 1px 5px 0 rgba(0,0,0,0.12),0 3px 1px -2px rgba(0,0,0,0.2);
color:#DDD;
margin-top:1em;
padding:1em
}

.floatingEntryContent .headerInfo {
background-color:#222;
border-bottom:1px solid #222;
box-shadow:0 2px 2px 0 rgba(0,0,0,0.14),0 1px 5px 0 rgba(0,0,0,0.12),0 3px 1px -2px rgba(0,0,0,0.2);
color:#DDD
}

.floatingEntryContent-tab {
background-image:none!important;
color:#FFF!important;
margin-left:0!important
}

.floatingEntryContent-tab > div {
background-color:#222!important;
opacity:1!important
}

.floatingEntryContentSlide {
max-width:100%!important;
width:97%
}

.floatingEntryContentSlide > div:last-child {
margin:0!important
}

.floatingEntryScroller::-webkit-scrollbar {
background-color:#333
}

.floatingEntryScroller::-webkit-scrollbar-thumb {
background-color:#444;
outline:1px solid #333
}

.floatingEntryScroller::-webkit-scrollbar-track {
box-shadow:inset 0 0 6px rgba(0,0,0,0.3)
}

.fx .form [type='text'] {
background-color:#444;
color:#CCC
}

.fx .form [type='text']:focus {
background-color:#444;
border-color:#0FADED
}

.fx .form.hercule-search .topic-list .item {
color:#CCC!important
}

.fx .row {
margin-bottom:1rem
}

.fx button.primary.pro {
background-color:#0FADED
}

.fx-button.secondary {
background-color:#222;
border-color:#0FADED!important;
border-radius:0;
box-shadow:0 2px 2px 0 rgba(0,0,0,0.14),0 1px 5px 0 rgba(0,0,0,0.12),0 3px 1px -2px rgba(0,0,0,0.2);
color:#0FADED!important;
transition:all .35s ease 0
}

.fx-button.secondary:hover {
background-color:#333;
transition:all .35s ease 0
}

.giant-mark-as-read {
background-color:#444!important;
border:0!important;
border-radius:0!important;
box-shadow:0 2px 2px 0 rgba(0,0,0,0.14),0 1px 5px 0 rgba(0,0,0,0.12),0 3px 1px -2px rgba(0,0,0,0.2)!important;
color:#000!important;
font-weight:400!important;
transition:all .35s ease 0
}

.giant-mark-as-read:hover {
background-color:#333!important;
color:#111!important;
transition:all .35s ease 0
}

.group-button > button > span {
color:#DDD!important
}

.headerInfo+.floatingEntryScroller {
background-color:#292929;
color:#DDD
}

.headerInfo-expanded-img {
background-color:#444!important;
margin:0 .05em
}

.heading {
color:#DDD
}

.hercule-search {
background:none!important;
background-color:#444!important;
border:0;
border-radius:0;
display:none
}

.label.nonEmpty {
color:#0FADED!important
}

.landing-old .section.top > h1 {
color:#DDD
}

.list-entries > h4 {
margin:1.5rem 0
}

.panel {
background-color:#2D2E32
}

.pinContainer {
transition:all .35s ease 0
}

.pinContainer:hover {
background-color:#2D2E32;
transition:all .35s ease 0
}

.popover {
background-color:#444!important;
border:0;
border-radius:0!important;
box-shadow:0 2px 2px 0 rgba(0,0,0,0.14),0 1px 5px 0 rgba(0,0,0,0.12),0 3px 1px -2px rgba(0,0,0,0.2)!important;
color:#DDD!important
}

.popover > .menu > div > .item {
color:#DDD!important;
transition:all .35s ease 0
}

.popover > .menu > div > .item:hover {
background-color:#333;
transition:all .35s ease 0
}

.read {
box-shadow:none!important
}

.read > .content > .metadata,.read > .content > .summary,.read > .content > a {
color:#555!important;
transition:all .5s ease 0
}

.relatedArticles.threeCards {
background-color:#333;
margin-top:1em;
padding:1em
}

.relatedArticles.threeCards > h4 {
margin:1rem 0!important
}

.right-col > .group-button {
background-color:#444!important;
border:0!important;
border-radius:0;
box-shadow:0 2px 2px 0 rgba(0,0,0,0.14),0 1px 5px 0 rgba(0,0,0,0.12),0 3px 1px -2px rgba(0,0,0,0.2);
transition:all .35s ease 0
}

.right-col > .group-button > .button-dropdown:hover {
background-color:#333;
border:0;
border-radius:0;
transition:all .35s ease 0
}

.right-col > .group-button > button:hover {
background-color:#333;
border:0;
border-radius:0;
transition:all .35s ease 0
}

.row > .col-xl-3 {
display:none
}

.row > .col-xl-9 {
width:100%
}

.section.top .fx-button.primary.pro {
background-color:#444;
box-shadow:0 2px 2px 0 rgba(0,0,0,0.14),0 1px 5px 0 rgba(0,0,0,0.12),0 3px 1px -2px rgba(0,0,0,0.2)!important;
color:#0FADED;
transition:all .35s ease 0
}

.section.top .fx-button.primary.pro:hover {
background-color:#333
}

.selected {
background-color:#333!important;
transition:all .35s ease 0
}

.selectorNewMarker {
border-color:#0FADED;
color:#0FADED
}

.subscriptionContentsTitle {
color:#DDD
}

.tab > .target {
background-color:#222;
color:#DDD!important;
transition:all .35s ease 0
}

.tab > .target:hover {
background-color:#444;
box-shadow:0 2px 2px 0 rgba(0,0,0,0.14),0 1px 5px 0 rgba(0,0,0,0.12),0 3px 1px -2px rgba(0,0,0,0.2);
transition:all .35s ease 0
}

.u100Entry {
margin:0!important;
max-width:100%
}
}
