css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(youtube-mp3.org){
html,body{
background-color: #292929;
font-family: "Lato", "Open Sans", sans-serif;
}
::selection{
background: #3D8EB9;
color: #fff;
}
::-moz-selection{
background: #3D8EB9;
color: #fff;
}
#content #inner h1 img{
display: none;
src: url("http://i.imgur.com/l9YAyqE.png");
margin-left: auto;
margin-right: auto;
}
#content .normal #loader{
display: block;
content: url("http://samherbert.net/svg-loaders/svg-loaders/three-dots.svg");
margin-left: auto;
margin-right: auto;
}
#content #inner{
background-color: #414040;
padding: 15px;
border-radius: 0.2em;
max-width: 500px;
min-width: 500px;
}
#form #youtube-url{
-webkit-appearance:none;
-moz-appearance: none;
box-shadow: none;
text-align: center;
border: 1px solid #292929;
padding: 4px;
border-radius: 0.2em;
background-color: #292929;
color: #eee;
margin-top: 50px;
outline: none;
}
#form #submit{
color: #eeeeee;
background: #71BA51;
border-color: transparent;
border-radius: 0.2em;
font-family: sans-serif;
margin-top: 10px;
-webkit-transition-property: background;
-moz-transition-property: background;
-o-transition-property: background;
transition-property: background;
-webkit-transition-duration: 0.5s;
-moz-transition-duration: 0.5s;
-o-transition-duration: 0.5s;
transition-duration: 0.5s;
-webkit-transition-timing-function: linear;
-moz-transition-timing-function: linear;
-o-transition-timing-function: linear;
transition-timing-function: linear;
}
#form #submit:hover{
color: #eeeeee;
background: #414040;
border-color: transparent;
border-radius: 0.2em;
font-family: sans-serif;
margin-top: 10px;
border: 1px solid #71BA51;
-webkit-transition-property: background;
-moz-transition-property: background;
-o-transition-property: background;
transition-property: background;
-webkit-transition-duration: 0.5s;
-moz-transition-duration: 0.5s;
-o-transition-duration: 0.5s;
transition-duration: 0.5s;
-webkit-transition-timing-function: linear;
-moz-transition-timing-function: linear;
-o-transition-timing-function: linear;
transition-timing-function: linear;
}
  #progress_info{
   border: 0;
    margin-right: -10px;
    color: #fff;
 }
  #progress_info a{
    display: block;
    margin-top: 15px;
    color: #fff;
    text-decoration: none;
    font-size: 20px;    
  }
  .success{
    background-color: #71BA51;
  }
  .error{
    background-color: #E01931;
  }
.normal{
background-color: #FEC606;
text-align: center;
}
#description,#footer{
  display:none;
}
}
