.date_header {
    counter-reset: subscriber note;
    /* Nous initialisons le compteur note */
}
.note_follow {
    text-align: center;
}
.note_follow>span {
    visibility: hidden;
}
.note_follow::before {
    /* Ajoute 1 au compteur subscriber */
    
    counter-increment: subscriber;
    content: counter(subscriber);
    color: #ffffff;
    font-weight: bold;
    font-size: 20px;
    line-height: 50px
}
span.activity::before {
    /* Ajoute 1 au compteur note */
    
    counter-increment: note;
    content: counter(note) " - ";
    color: #aaaaaa;
    margin-top: 10px;
    margin-left: 7px;
    font-weight: bold;
}
