css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("sidereel.com") {
  body{
    background: #19191a;
    color: #37AAE9;
  }
  
  .sidebar{
    display: none;
  }
  .main{
    padding: 20px 20px 20px 20px;
  }
  #tracked-shows-container .tracker-details .next-content, #tracked-shows-container .tracker-details .ep-guide-content{
    background: #161718;
    border:1px solid #070708;
  }
  
  #tracked-shows-container .episodes-tab.selected{
    background: #161718;
    border: 1px solid #0a0b0c;
    color: #BDC4CB;
  }
  #tracked-shows-container .episodes-tab{
    color: #868181;
    background: #18191b;
    border: 1px solid #020202;
  }
  nav{
    background: #0d2839;
  }
  #tracker-stats-container{
    background: #161718;
    border: 1px solid #020202;
    border-top-color: #020202;
    border-bottom-color: #020202;
  }
  
  .partial-bordered-module{
    background: #161718;
  }
  .bordered-module, .partial-bordered-module{
    border: 1px solid #252627;
    border-top-color: #2c71ab;
    border-bottom-color: #5d391b;
  }
  #tracked-shows-container .tracked-show-container{
    border: 0px;
    border-top: solid 1px #714b26;
  }
  #tracked-shows-container .show-info{
    background: #1e2023;
  }
  .show-name a{
    color: #cdd3d7;
    margin: 5px 0px 5px 20px;
  }
  #tracked-shows-container .show-name{
    font-size: 20px;
    margin-bottom: 10px;
    margin-top: 8px;
  }
  footer{
    background: #272729;
  }
  .calendar-wrapper .slick-slide .calendar-entry:nth-child(2n+1){
    background-color: #1e2023;
  }
  .calendar-wrapper .slick-slide .calendar-entry .calendar-entry-content a.episode-title{
    color: #989292;
  }
  .calendar-wrapper .slick-slide .calendar-entry .calendar-entry-content a.show-title{
     color: #cdd3d7;
  }
}
