css:
@-moz-document domain("hypem.com") {

#content-right {
    display: none;
}

#header {
    background-color: #83c441;
}

#menu-item-stack a {
    display: none;
}

#menu-item-supportus a {
    display: none;
}

#menu-username {
    background-color: #83c441;
    border-color: #ffffff;
    height: 43px;
	margin-right: 181px;
}

#menu-username > ul {
    margin-top: 2px;
	margin-right: 160px;
}

#message h1 {
    color: #000000;
}

#nav-artists a {
    font-size: 16px;
}

#nav-favorites a {
    font-size: 16px;
}

#nav-feed a {
    font-size: 16px;
}

#nav-genres a {
    font-size: 16px;
}

#nav-history a {
    font-size: 16px;
}

#nav-lastweek a {
    font-size: 16px;
}

#nav-no_remixes a {
    font-size: 16px;
}

#nav-obsessed a {
    font-size: 16px;
}

#nav-playlist-1 a {
    color: #ffffff;
    font-size: 16px;
}

#nav-playlist-2 a {
    color: #ffffff;
    font-size: 16px;
}

#nav-playlist-3 a {
    color: #ffffff;
    font-size: 16px;
}

#nav-remixes a {
    font-size: 16px;
}

#nav-remixes_only a {
    font-size: 16px;
}

#nav-videos a {
    font-size: 16px;
}

#player-container {
    height: 60px;
}

#player-nowplaying {
    font-family: Helvetica !important;
    margin-top: -3px !important;
    font-size: 16px !important;
    letter-spacing: 1px !important;
}

#player-timebar {
    margin-left: 275px;
}

#playerPlay {
    font-size: 25px;
}

#section-track-2msrj {
    background-color: #e4ffde !important;
}

#search-form {
    margin-right: 310px;
}

#sponsored_tracks {
    display: none !important;
}

#submenu-filter {
    font-size: 16px !important;
}

#player-container #player-inner #player-controls #playerFav {
    font-size: 15px !important;
}

.section-track.haarp-active {
    background-color: #e4ffde !important;
}

a.haarp-fav-ctrl.icon-heart.fav-off {
    font-size: 25px;
    margin-right: -5px;
}

a.haarp-fav-ctrl.icon-heart.fav-on {
    font-size: 25px;
    margin-right: -5px;
}

a.logo-txt.icon-logo {
    color: #000000;
}

a.play-ctrl.icon-toggle.haarp-play-ctrl.pause {
    font-size: 30px;
}

a.play-ctrl.play.icon-toggle.haarp-play-ctrl {
    border-right-width: -25px;
    font-size: 30px;
}

a.selected {
    font-size: 16px;
}

a.thumb {
    background-color: #d62990 !important;
}

div.favcountlist {
    display: none;
}

div.profile h1 {
    color: #ffffff !important;
}

div.section.section-track.haarp-section-track.haarp-play-ctrl.even {
    height: 100px;
}

div.section.section-track.haarp-section-track.haarp-play-ctrl.first {
    height: 100px;
}

div.section.section-track.haarp-section-track.haarp-play-ctrl.first.haarp-active {
    background-color: #e4ffde !important;
    height: 100px !important;
}

div.section.section-track.haarp-section-track.first.haarp-fav-active {
    height: 100px !important;
}

div.section.section-track.haarp-section-track.haarp-play-ctrl.first.haarp-fav-active {
    height: 100px !important;
}

div.section.section-track.haarp-section-track.first {
    height: 100px !important;
}

div.section.section-track.haarp-section-track.haarp-play-ctrl.odd {
    background-color: #dbdbdb !important;
    height: 100px !important;
}

div.section.section-track.haarp-section-track.haarp-play-ctrl.odd.haarp-active {
    background-color: #e4ffde !important;
}

h3.track_name {
    color: #000000;
}

li.favdiv {
    margin-right: -40px;
    margin-top: 80px;
}

li.playlist-ctrls.playlist-on {
    font-size: 30px;
}

p.post_info {
    color: #000000;
}

span.title {
    margin-right: -15px;
}

ul.tools {
    margin-right: -20px;
    margin-top: 20px;
}

div.meta {
    display: none !important;
}

div.section.section-track.haarp-section-track.odd {
    height: 100px;
    background-color: #dbdbdb;
}

div.section.section-track.haarp-section-track.even {
    height: 100px;
}

a.haarp-fav-count.toggle-favorites.favcount-on {
    color: #b0b0b0;
}

div.track-info.completed {
    font-style: italic !important;
    color: #616161 !important;
}

div.track-info {
    color: #6b6b6b;
    font-size: 12px;
    font-weight: bold;
    text-transform: capitalize;
    font-style: italic;
    letter-spacing: .2px;
}

div.user.header-box {
    display: none;
}

a.user {
    font-size: 20px;
}

div.section.section-track.haarp-section-track.odd.haarp-active {
    background-color: #e4ffde;
}

div.unit_head {
    display: none;
}

div.previous-premieres.on-bandcamp {
    display: none;
}

#content > div:nth-child(3) {
    display: none;
}

}
