@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://soz6.com/panel/messages") {
  strong > a {
    color: #363B42 !important;
  }
}

@-moz-document url("http://soz6.com/bildirimler") {
  #chargermain > div, #rightcol > div   {
    background-color: #363B42 !important;
  }
  #chargermain > div a, .rightcolumn > div a {
    color: #7dc6c4 !important;
  }
  #chargermain > div > span {
    color:  #C2C2C7 !important;
  }
  .rightcolumn {
    padding-top: 8% !important;
  }

}
@-moz-document domain("soz6.com") {
  
  .body {
    background-color: #272B30 !important;
    color: #C2C2C7 !important;
    font-size: 13px !important; 
    line-height: 20px !important;
    font-family: Helvetica, Arial, Sans-Serif;
  }
  
  .body a {
    color: #c1ccbd !important;
  }
  .logo {
    padding-bottom: 5px !important;
  }
  
  #header {
    background: #363B42 !important;
  }

  #header a {
    color: #c0cec0 !important;
  }
  #header a:hover {
    color: #51c1b7 !important;

  }
  #header {
    height: 60px !important;
  }
  .nav {
    margin-top: 35px;
    
  }
  #header > .nav > .liste a {
     font-weight: 600 !important;
  }
  
  .leftfirst {
    padding-top: 10px !important;
    padding-left: 1% !important;
    padding-right: 1% !important;
    width: 18% !important;
    
  }

  #content {
    width: 57% !important;
    margin-left: 2% !important;
  }
  .rightcolumn {
    width: 18% !important;
    padding-left: 1% !important;
    padding-right: 1% !important;
  }


  #main {
    padding-top: 10px !important;
        font-size: 14px;
  }
  #main > #chargermain div {
    color: #c0cec0 !important;
    
  }
  #chargermain > a, #chargermain > h1 > a, .entry > a {
    color: #66bb6a !important;
  }


  .infoauth > a, .entrybuttons > a {
    color: #81c784 !important;
  }
  .infoauth > a:hover, .entrybuttons > a:hover {
    color: #a5d6a7 !important;
  }


  #headerfinder {
    color: #424242 !important;
    background-color: #D4D7DB !important;
  }
  
  #aciklama, #mcontent {
    background-color: #6C6F74 !important;
  }
  .entry {
    font-size: 14px;
    text-align: justify;
  }
  .mesajdaha {
    color: #c8e6c9 !important;
    background-color: #373c42 !important;
  }
  .gbutton, input {
    color: #006064 !important;
    background-color: #D4D7DB !important;
  }
  #tabsH > ul > li a span{
    background-color: #009688 !important;
    color: #a7ffeb !important;
  }
  
  .yaz {
    background-color: #363B42 !important;
    padding-top: 2px !important;
    padding-bottom: 2px !important;
  }
  .avatar {
    float: right;
  }
 

}
