css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
@-moz-document url("chrome://echofon/content/sidebar.xul"),
url("chrome://echofon/content/panel_fx3.xul"),
url("chrome://echofon/content/window.xul") {
    * {
        /*box-sizing:border-box;*/
    }
    .echofon-tab[checked="true"] {
        background: #000;
        border: 1px dotted #666!important;
    }
    echofon-status,
    .echofon-thread-container {
        background: #333;
        border-bottom: 1px dashed #666;
    }
    #echofon-toolbar-body,
    #echofon-menubar {
        background-image: url('data:image/gif;base64,R0lGODlhGAAYAJEAAB8dHSEfHyIhIf///ywAAAAAGAAYAAAClwSEGLcasp4DoSZoFWbidGUklEFFEil9VQlBo9tVaxSDMaWprHeEJuN5LHCYVCMEO5mEHdwwheMMPTGmr/WQZnulrrX2ob5GYtq20YSOZhdujuRuJYGuJQnBMEc/LgNTh2IRNMRHiDc1c8JzRAP3s6LBRShW0pB1QQiZJ/OCpSQklYe38fQDFhGYtdKj2UIGusVy+GLxUAAAOw==')!important;
        border: 0;
    }
    .echofon-status-unread-border-container,
    .echofon-status-unread-border,
    .echofon-status-unread-message {
        background: #000;
    }
    .echofon-status-unread-message {
        text-transform: uppercase;
        font-size: 0.9em;
        color: #555;
    }
    .echofon-status-body,
    .echofon-status-message {
        color: #fff;
    }
    .echofon-status-additional-screen-name {
        color: #888!important;
    }
    .echofon-hyperlink {
        color: #93e100!important;
    }
    echofon-status-tagline,
    .echofon-status-timestamp,
    .echofon-source-link,
    echofon-status-retweet-status {
        padding-top: 0!important;
        text-transform: uppercase;
        font-size: 1em!important;
        color: #888!important;
    }
    .echofon-status-info {
        padding-top: 0.5em;
        text-transform: uppercase;
        font-size: 0.7em!important;
        color: #888!important;
    }
    .echofon-status-timestamp,
    .echofon-source-link {
        font-size: 1em!important;
    }
    .echofon-status-body:nth-child(2) {
        /*padding-top:0.3em;*/
    }
    /*[mode="panel"] .echofon-status-usericon {
     width:50px;
     height:50px;
     background-size:50px 50px !important;
     border-radius:0.5em;
   }*/
    .echofon-status-user {
        display: none;
    }
    .echofon-status-additional-screen-name {
        font-weight: bold;
        color: #9ff400!important;
    }
    .echofon-compose-bar-container,
    .echofon-textbox-container,
    #echofon-textbox,
    .textbox-input-box,
    .textbox-hidden-text,
    ._textbox-textarea,
    .anonymous-div,
    .wrap,
    #echofon-compose-bar-attach-menu-box[value="140"],
    .echofon-compose-bar-container,
    #echofon-compose-bar-textbox,
    #echofon-account-menu-box,
    #echofon-timeline-box,
    #echofon-search-container-wrap,
    #lists-timeline,
    echofon-dm-thread,
    #echofon-search-bar-container,
    #echofon-add-search-button,
    .anonymous-div,
    .echofon-compose-popup * {
        background: #000!important;
        border: 0!important;
        color: #fff!important;
        padding: 0!important;
    }
    .echofon-compose-popup * {
        text-transform: none!important;
        font-size: 1.04em!important;
        padding-bottom: 0.2em!important;
    }
    .echofon-search-cell-title,
    echofon-saved-search,
    echofon-trend {
        background: #333!important;
        padding: 0.7em 0.5em!important;
        text-shadow: #000 1px 1px!important;
        color: #fff!important;
        border: 0!important;
        border-bottom: 1px dashed #666!important;
    }
    .echofon-thread-container {
        padding: 0.7em 0.5em!important;
    }
    #echofon-search-bar-container {
        padding-left: 1em!important;
    }
    .textbox-hidden-text,
    ._textbox-textarea,
    xul[anonid=inReplyToField] {
        background: #000!important;
    }
    echofon-status[highlighted="true"] {
        background: #454545!important;
    }
    .echofon-function-icon-box {
        z-index: 99!important;
    }
    [id|=echofon-event] {
        background: #000!important;
        border-bottom: 1px dashed #666!important;
    }
}
