css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("^https://searx.*/preferences") {
  ul.nav-tabs li {
    padding: 0 2px 0 2px !important;
  }
}
@-moz-document regexp("^https://searx.*") {
  * {
    background: #1E2121 !important;
    border-color: #5C5C5C !important;
    font-size: 99% !important;
    font-family: Ubuntu, 'glyphicons halflings' !important;
    font-weight: normal !important;
  }
  body {
    color: #666 !important;
  }
  a {
    color: #f27f4 !important;
    text-decoration: underline !important;
  }
  #q {
    font-size: 1rem !important;
  }
  #q:focus {
    color: whitesmoke !important;
  }
  a[href="/"] {
    color: white !important;
  }
  div.container {
    min-width: 90% !important;
  }
  div.external-link {
    color: #F27F4E !important;
    font-size: 50% !important;
  }
  div.navbar,
  div.navbar-default,
  div.menu {
    border-top: none !important;
  }
  div.alert-danger {
    width: 60% !important;
    position: relative !important;
    margin: 10px 20% 0 20% !important;
  }
  button.close,
  button.close span {
    font-size: 15pt !important;
    color: #666 !important;
    text-shadow: none !important;
    opacity: 1 !important;
  }
  div.alert-danger ul {
    padding: 0 !important;
    list-style: none !important;
  }
  button.btn-default {
	color: #5c5c5c !important;
  }
  div.input-group > span.input-group-btn:hover > button.btn-default,
  div.input-group > span.input-group-btn:active > button.btn-default {
	color: #d2d2d2 !important;
  }
  #time-range {
    margin: 0 3px 3px 0 !important;
  }
  #categories label {
    margin: 4px 4px 0 0 !important;
    padding-bottom: 6px !important;
    border-right: solid 1px #6c6c6c !important;
  }
  #categories input[type="checkbox"]:checked + label,
  .search_categories input[type="checkbox"]:checked + label {
    border: solid 1px #50915c !important;
    box-shadow: 0 0 0 2px #50915c !important;
    color: #D2D2D2 !important;
  }
  span.glyphicon.glyphicon-search,
  span.glyphicon.glyphicon-forward,
  span.hide_if_nojs {
	height: 16px !important;
    width: 16px !important;
    background: transparent !important;
  }
  h1,
  h2,
  h3,
  h4,
  h5,
  h6 {
    font-family: Ubuntu !important;
  }
  div#sidebar_results * {
    color: #6C6C6C !important;
    font-family: Ubuntu !important;
    margin-top: 10px !important;
  }
  div.input-group > span.input-group-btn {
    border: solid 1px #5c5c5c !important;
    padding: 0 5px 0 2px !important;
  }
  div.input-group > span.input-group-btn:hover,
  div.input-group > span.input-group-btn:active,
  div.input-group > span.input-group-btn:hover > button,
  div.input-group > span.input-group-btn:active > button {
    background: -linear-gradient(top,#72cf83,#4F9262) !important;
    background: -moz-linear-gradient(top,#72cf83,#4F9262) !important;
    background: -webkit-linear-gradient(top,#72cf83,#4F9262) !important;
    border-color: #4F9262 !important;
    cursor: pointer !important;
  }
  div.input-group > span.input-group-btn > button {
    border: none !important;
    width: 35px !important;
    height: 30px !important;
  }
  .result,
  .result-default,
  .result-videos,
  .result-torrent {
    padding: 25px 15px 25px 15px !important;
    border: solid 1px transparent !important;
  }
  .result:hover,
  .result-default:hover,
  .result-videos:hover,
  .result-torrent:hover {
    border-color: #5c5c5c !important;
  }
  .result form.suggestion_item button {
    color: #a94442 !important;
  }
  .result {
    min-height: 90px !important;
  }
  .result-content {
    color: #6B6B6B !important;
  }
  .result_header * {
    color: whitesmoke !important;
    font-family: Ubuntu !important;
    font-weight: normal !important;
  }
  div.external-link {
    min-height: 16px !important;
  }
  span.highlight {
    font-weight: normal !important;
    text-decoration: underline !important;
  }
  div.pull-left *,
  div.pull-right * {
    color: #5c5c5c !important;
  }
  div.pull-right form button.btn.btn-default:hover,
  div.pull-right form button.btn.btn-default:active {
    background: #4F9262 !important;
    border-color: #4F9262 !important;
    color: whitesmoke !important;
  }
  button.btn-xs {
    white-space: pre-wrap !important;
    text-align: justify !important;
  }
  select.form-control,
  div.col-xs-12.col-md-8 * {
    height: 48px !important;
  }
  div.col-xs-6 {
    padding-right: 2px !important;
    padding-left: 2px !important;
  }
  div.row {
    max-width: inherit !important;
    margin: 0 !important;
  }
  select.custom-select:focus,
  input#search_url:focus {
	box-shadow: none !important;
    color: #777 !important;
    border-color: #777 !important;
  }
  select.custom-select:hover,
  div#categories label:hover {
	cursor: pointer !important;
  }
  div.modal-wrapper * {
	color: #666 !important;
  }
}
