css:
@-moz-document url-prefix("http://www.google"), domain("www.google.co.in") {
    body {
        background: #1DE9B6 !important;
    }
    .jhp input[type="submit"],
    .sbdd_a input,
    .gbqfba {
        background-image: -webkit-gradient(linear, left top, left bottom, from(#00BFA5), to(#00BFA5)) !important;
        background-image: -webkit-linear-gradient(top, #00BFA5, #00BFA5)!important;
        -webkit-border-radius: 2px;
        -webkit-user-select: none;
        background-color: #00BFA5!important;
        border: 1px solid #00BFA5!important;
        border-radius: 2px;
        color: #ffffff !important;
        cursor: default;
        font-family: arial, sans-serif;
        font-size: 13px;
        font-weight: bold;
        margin: 11px 4px;
        min-width: 54px;
        padding: 0 16px;
        text-align: center;
        opacity: 0.87 !important;
    }
    .jhp input[type="submit"]:hover,
    .sbdd_a input,
    .gbqfba {
        background-image: -webkit-gradient(linear, left top, left bottom, from(#00BFA5), to(#00BFA5)) !important;
        background-image: -webkit-linear-gradient(top, #64FFDA, #64FFDA)!important;
        -webkit-border-radius: 2px;
        -webkit-user-select: none;
        background-color: #64FFDA!important;
        border: 1px solid #64FFDA!important;
        border-radius: 2px;
        color: #ffffff !important;
        cursor: default;
        font-family: arial, sans-serif;
        font-size: 13px;
        font-weight: bold;
        margin: 11px 4px;
        min-width: 54px;
        padding: 0 16px;
        text-align: center;
    }
    #fbar {
        background: #00BFA5 !important;
        border-top: 1px solid #1DE9B6 !important;
        line-height: 40px;
        min-width: 980px;
        opacity: 0.87 !important;
    }
    .ftl,
    #footer a {
        color: #ffffff !important;
        margin: 2px 10px 0;
        opacity: 0.87 !important;
    }
    #_eEe {
        color: #ffffff !important;
        display: inline-block;
        line-height: 28px;
        opacity: 0.87 !important;
    }
    a:visited {
        color: #609;
    }
    a {
        color: #ffffff !important;
        text-decoration: none;
    }
    .sfbgg {
        background-color: #00BFA5 !important;
        height: 65px;
    }
    #hdtbSum {
        background: #00BFA5 !important;
        height: 58px;
        padding: 0;
        position: relative;
        z-index: 102;
        opacity: 0.87 !important;
    }
    #hdtb-msb .hdtb-mitem.hdtb-msel,
    #hdtb-msb .hdtb-mitem.hdtb-msel-pre {
        border-bottom: 3px solid #A7FFEB!important;
        color: #ffffff !important;
        font-weight: bold;
        opacity: 0.87 !important;
    }
    #appbar {
        box-sizing: border-box;
        width: 100%;
        background: #64FFDA!important;
        opacity: 0.87 !important;
    }
.hdtb-dd-mn a, .hdtb-dd-mn a:visited, .hdtb-dd-mn a:active {
    display: block;
    text-decoration: none;
    background: #00BFA5 !important;
}
#hdtb-tls:hover {
    background-color: #1DE9B6;
    background-image: -webkit-gradient(linear,left top,left bottom,from(#1DE9B6),to(#1DE9B6))!important;
    background-image: -webkit-linear-gradient(top,#1DE9B6,#1DE9B6)!important;
    background-image: linear-gradient(top,#1DE9B6,#1DE9B6)!important;
    border: 1px solid #1DE9B6!important;
    color: #ffffff;
    opacity: 0.87 !important;
}
}
