css:
@-moz-document url-prefix("http://www.nexusmods.com/fallout4") {
#hotfiles-title {
    background-color: #FFF;
    color: #333;
    padding: 5px;
    line-height: 1em;
}
#hotfilesbar {
    border-bottom: 1px solid #888;
    margin-bottom: 5px;
    padding-bottom: 5px;
}
.news-list li,
.files-list li > div {
    border: 1px solid #DDD;
    border-radius: 3px;
    /*background-color: rgba(77,77,77,0.2);*/
    
    background: none;
    box-shadow: 2px 2px 5px 0px rgba(77, 77, 77, .2);
    padding: 0;
    /*margin-bottom: 5px*/
}
.news-list li {
    margin-bottom: 5px;
    background-color: #EEE;
}
.news-list li .date-comments {
    border-top-left-radius: 3px;
    border-top-right-radius: 3px;
}
.news-list li .bb-content {
    padding: 5px;
}
.files-list li > div:hover {
    border: 1px solid #aaa;
    box-shadow: 2px 2px 5px 0px rgba(77, 77, 77, .5);
}
.news-list li h2,
.files-list li > div a.file,
.files-list li .file {
    background: none;
    color: #666;
    text-shadow: none;
    border-bottom: 1px dashed #AAA;
    margin-bottom: 5px;
    /*padding:0;*/
}
.files-list a.file:hover {
    color: #333;
}
.files-list a.file:visited::after {
    content: '[SEEN]';
}
.files-list a.file .file-arrow {
    display: none;
}
.files-list .file-info {
    border-top: 1px solid #BBB
}
.files-list .file-image,
.file-image-scroller {
    padding: 5px;
    box-shadow: 0 2px 3px -1px rgba(77, 77, 77, .5) inset, 0 -2px 3px -1px rgba(77, 77, 77, .5) inset;
    background-color: rgba(77, 77, 77, .1)
}
.file-image-scroller .file-image-arrow.file-prev,
.file-image-scroller .file-image-arrow.file-next {
    border: 1px solid #ddd;
}
.file-image-scroller .file-image-arrow.file-prev {
    border-width: 1px 1px 1px 0;
    background-position: -300px -590px;
}
.file-image-scroller .file-image-arrow.file-next {
    border-width: 1px 0 1px 1px;
    background-position: -350px -590px;
}
.file-image-scroller .file-image-container {
    background: transparent;
}
}
