css:
@-moz-document domain("knowyourmeme.com") {
a {
	text-decoration: underline;
}
*, .related_memes .name a:hover, .related_memes .name a {
	background-color: black!important;
	color: white!important;
}
#cboxCurrent, #trending-bar h5 {
	color: #DDD!important;
}
/*kym used images for the popup border*/
#cboxBottomLeft, #cboxBottomCenter, #cboxBottomRight, #cboxMiddleLeft, #cboxMiddleRight, #cboxTopLeft, #cboxTopCenter, #cboxTopRight, a.button, .blueBar, .bb_item, .tagit, .tagit * {
	background-image: none !important;
}
#cboxOverlay {
	opacity: .75!important;
}
#colorbox {
	border-radius: 40px!important;
}
#colorbox * {
	background-color: transparent!important;
}
#cboxPrevious, #cboxNext, #cboxClose {
	filter: invert();
}
.info {
	background-color: rgba(0, 0, 0, 0.75)!important;
}
.info * {
	background-color: transparent!important;
}
/* the tumb down icon has a white background and the thumb up hasn't for some reason. So for the thumb down icon we use the up icon and rotate it.*/
.thumb_mini.down {
	background-position: -0px -60px!important;
	transform: rotate(180deg)!important;
}
}
