css:
@-moz-document url("https://epicmafia.com/lobby"), url-prefix("https://epicmafia.com/lobby") {
body {
    position: absolute;
    top: 0px;
    left: 0px;
    height: 100%;
    width: 100%;
    visibility: hidden;
    z-index: -9;
}
#lobby_sticky_setups {
    visibility: visible;
    position: absolute;
    top: 120%;
    left: 67%;
    padding: 1%;
    background-color: white;
    border-radius: 2%;
}
#navbox_inner {
    visibility: visible;
    position: absolute;
    top: 128%;
}
#lobbychat h2.separator {
    position: absolute;
    top: 14%;
    left: 64%;
    z-index: -1;
    background-color: white;
}
#lobbychat h2.separator div {
    visibility: visible;
    margin: 0px;
}
a[data-title~=emojis] {
    visibility: visible;
    position: absolute;
    top: 2%;
    left: 100%;
}
#emojibox {
    padding-bottom: .5%;
    background-color: darkgray;
    border-radius: 2%;
    border: double 2px black;
    width: 20.5%;
    visibility: visible;
    position: absolute;
    left: 78.5%;
    top: 14%;
}
#emojibox span {
    font-size: 18px;
    width: 18px;
    height: 18px;
}
#lobby_container #lobbychat #window {
    visibility: visible;
    position: absolute;
    top: 14%;
    left: 29%;
    height: 83% !important;
    max-width: 49%;
    width: 49%;
    min-width: 49%;
    border-radius: 1%;
    z-index: -2;
    overflow: hidden;
}
#lobby_container #lobbychat #window #window_i {
    background-color: white;
    visibility: visible;
    height: 92% !important;
    width: 100%;
    z-index: -2;
    font-size: 100%;
    overflow-y: auto;
}
#window_i a > img {
    margin: 1%;
    max-width: 98%;
}
#lobby_container #lobbychat #userlist {
    visibility: visible;
    height: 91%;
    min-height: 91%;
    width: 96%;
    min-width: 96%;
    z-index: -2;
}
#chatbar {
    visibility: visible;
    min-width: 96%;
}
#lobby-poll {
    visibility: visible;
    position: absolute;
    top: 49%;
    left: 79%;
    background-color: white;
    padding: 1%;
    border-radius: 2%;
    width: 18%;
    z-index: -2;
}
#lobby_recent_topics {
    visibility: visible;
    position: absolute;
    top: 14%;
    left: 79%;
    padding: 1%;
    width: 18%;
    border-radius: 2%;
    background-color: white;
    z-index: -1;
}
#commentbox_lobby_5 {
    visibility: visible;
    width: 96%;
    padding: 1%;
    border-radius: 2%;
}
#header {
    position: absolute;
    width: 98%;
    top: 2%;
    left: 1%;
    visibility: visible;
    border-radius: 2%;
}
#lobbyinfo_box {
    background-color: white;
    padding: 0.2%;
    border-radius: 2%;
    visibility: visible;
    position: absolute;
    top: 14%;
    left: 1%;
    width: 27%;
}
#lobby {
    visibility: visible;
    border-radius: 2%;
    position: absolute;
    top: 120%;
    left: 2%;
    width: 64%;
    z-index: -3;
}
#lobby_banner img {
    max-width: 98%;
}
div.tip_voteusers {
    visibility: visible;
    z-index: 1;
}
#gameinfo {
    visibility: visible;
    position: absolute;
    top: 147%;
    left: 67%;
    width: 32%;
    max-width: 32%;
}
i.icon-plus {
    display: none;
}
}
