css:
/* STRICTLY MADE FOR THESKWADHANGOUT */
/* WEBSITES THAT THE CSS WORKS ON */
@-moz-document regexp(".*tinychat.com\/(?!home)[A-Za-z0-9_]+") {

/* Background CSS */
html, #newtab-window {
background: url("http://i.imgur.com/0IT0o78.png") #222 !important
}
body {background: transparent !important}
*:before, *:after {
    background-color: transparent !important; 
    border-color: #444 !important
}
/* Tinychat CSS */
body, #room, #chat, #left_block, #container, #page, #wrapper, #tinychat, #tinychat_sub {
    padding: 0px !important; 
    margin: 0px !important; 
    height: 100% !important; 
}
#img, #left, #footer, #share-bar, #body_footer_ad, #tcad_container, #iframe_ad, a[href*="/payment/upgrade_to_pro/step1"] {	
    display: none; 
    padding: 0px !important;	
}
#right_block {
    width: 300px;
    display: none;
    float: right;
    vertical-align: middle;
}
#left_block, #chat {
    width: 100% !important; 
}
#header {
    /* background: rgb(37,90,181); */
    background: -moz-linear-gradient(top, rgba(37,90,181,1) 0%, rgba(17,59,137,1) 5%, rgba(15,51,109,1) 100%);
    /* background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(37,90,181,1)), color-stop(5%,rgba(17,59,137,1)), color-stop(100%,rgba(15,51,109,1))); */
    background: -webkit-linear-gradient(top, rgb(37, 90, 181) 0%,rgba(17, 59, 137, 0.56) 5%,rgba(15, 51, 109, 0.85) 100%);
    /* -webkit-box-shadow: 0 2px 2px rgba(0, 0, 0,0.3); */
    -moz-box-shadow: 0 2px 2px rgba(0, 0, 0,0.3);
    /* box-shadow: 0 2px 2px rgb(255, 255, 255); */
    /* min-height: 43px; */
    /* -webkit-border-radius: 3px; */
    -khtml-border-radius: 3px;
    -moz-border-radius: 3px;
    /* border-radius: 3px; */
    /* position: relative; */
    margin: 0px 0;
}
#room_header {
	background: url("http://i.imgur.com/0IT0o78.png") #222 !important
}
#navigation {
    padding: 10px;
    float: left;
}
#chat {
    bottom:-5px !important; 
}
#logo {
    padding: 10px;
    float: right;
}
#website a {
	color: #00fb89;
    margin-left: 5px;
}
#website a:hover {
	color: #008c4c;
	text-decoration: underline;
}
#room_info h2.bio {
    color: #00fb89;
    font-size: 13px;
}
}
