css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("etsy.com") {
 
  .gnav-header #etsy-logo a {
    display: block;
    width: 100%;
    height: 100%;
    background: url('https://i.imgur.com/RqMvNId.png') transparent no-repeat center center;
    background-size: 100%;
    color: #2a2836!important;
    margin-top: 2px;
    text-indent: -10000em;
    overflow: hidden;
  }
  .ui-toolkit .gnav-header, .gnav-header {
    border-bottom: 1px solid #2a2836;
    -webkit-box-shadow: none;
    box-shadow: none;
  }
.apply-nav-height .sell-on-etsy-link a:link {
    color: #53dd6c!important;
    margin-right: 7px;
    border: 1px solid #53dd6c;
  }
  .gnav-header .register-link a:link, .gnav-header .sign-in-link a:link {
    color: #fe5c51;
  }
  .gnav-header .register-link a {
    margin-right: 7px;
    border: 1px solid #fe5c51;
  }
  .no-touch .gnav-header .sign-in-link > a:hover {
    background: #2a2836!important;
  }
  .nav-link .nav-icon {
    display: block;
    height: 32px;
    line-height: 32px;
    text-align: center;
  color: #00bcda;
    float: none;
    font-size: 20px;
    min-width: 20px;
    top: auto;
    position: static;
    margin: 18px auto 0;
    width: auto;
    overflow: visible;
    text-indent: 0;
    background: none;
  }
  .gnav-header .text-link {
    white-space: nowrap;
    color: #00bcda;
    font-size: 12px;
  }
  .nav-link:hover .nav-icon, .nav-link:focus .nav-icon {
    color: #fe5c51;
  }
 .gnav-header .nav-link:hover .text-link {
    color: #fe5c51;
  }
  .gnav-header #search-query {
    position: absolute;
    width: 100%;
    top: 50%;
    border: 1px solid #211e27;
    background: #211e27;
    margin-top: -17px;
    padding: 6px 10px;
    margin-left: 0;
    height: 34px;
    font-size: 14px;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    -webkit-box-shadow: inset 0 1px 2px #E9E9E8;
    box-shadow: inset 0 1px 2px #E9E9E8,0 0 0 1000px #fbfbfa inset;
  }
  .ui-toolkit .btn-primary {
    background-color: #00bcda;
    border-color: rgba(0, 0, 0, 0.1);
    color: #21202b;
  }
  .ui-toolkit .btn-primary:hover, .ui-toolkit .btn-primary.is-hovering {
    background-color: #fe5c51;
    border-color: rgba(0, 0, 0, 0.2);
  }
  .hero {
    background-color: #2a2836;
    background-image: url(https://i.imgur.com/IYYyJM6.jpg);
    background-size: 100% auto;
    background-size: cover;
    background-position: center center;
    background-repeat: no-repeat;
    border-top: 1px solid #2a2836;
    height: 0;
    padding-bottom: 42%;
    position: relative;
    z-index: 2;
    -ms-filter: "progid:DXImageTransform.Microsoft.AlphaImageLoader(src='/images/sell/sell-hero.jpg', sizingMethod='scale')";
  }
  .sticky-navigation .sticky-navigation-link {
    color: #FFF;
    text-shadow: 2px 2px 10px #FFF;
    outline: 0;
  }
  .sticky-navigation.is-fixed {
    position: fixed;
    top: 0px;
    bottom: auto;
    background-color: #2e2c3b;
    border-bottom: 1px solid #2e2c3b;
  }
  .ui-toolkit .panel {
    border-radius: 3px;
    background-color: #2a2836;
    border: 1px solid #2a2836;
    border-bottom-color: #2a2836;
    margin: 0 0 12px;
  }
  .sticky-navigation .sticky-navigation-link .sticky-navigation-bar {
    -webkit-transition: top 0.2s ease-out;
    -moz-transition: top 0.2s ease-out;
    -o-transition: top 0.2s ease-out;
    -ms-transition: top 0.2s ease-out;
    transition: top 0.2s ease-out;
    background: #53dd6c;
    display: block;
    height: 4px;
    left: 0;
    margin-top: 7px;
    position: absolute;
    top: 150%;
    width: 100%;
  }
  #footer-hero {
    padding-top: 6%;
    background-color: #2a2836;
    background-image: url(https://i.imgur.com/IYYyJM6.jpg);
    background-size: 100% auto;
    background-size: cover;
    padding-bottom: 0;
    background-position: center bottom;
    background-repeat: no-repeat;
    position: relative;
    min-height: 0;
    height: 430px;
    -ms-filter: "progid:DXImageTransform.Microsoft.AlphaImageLoader(src='/images/sell/sell-footer.jpg', sizingMethod='scale')";
  }
  .ui-toolkit .bt-xs-1 {
    border-top: 1px solid #2a2836;
  }
  .gnav-header {
    -webkit-font-smoothing: subpixel-antialiased;
    position: relative;
    margin: 0 -10px;
    padding: 0;
    background: #21202b;
    border-bottom: 1px solid #21202b;
    border-bottom: 0 solid rgba(0, 0, 0, 0);
    -webkit-box-shadow: 0 1px 4px rgba(0, 0, 0, 0.1),0 0 0 1px rgba(0, 0, 0, 0.05);
    box-shadow: 0 1px 4px rgba(0, 0, 0, 0.1),0 0 0 1px rgba(0, 0, 0, 0.05);
    z-index: 1000;
    min-width: 1000px;
  }
  body.gradient-bg {
    background: #2e2c3b!important;
    background-color: #2e2c3b!important;
  }
  .ui-toolkit .global-nav {
    -webkit-box-shadow: 0 1px 1px 0 rgba(0, 0, 0, 0.1);
    box-shadow: 0 1px 1px 0 rgba(0, 0, 0, 0.1);
    background-color: #2e2c3b!important;
  }
  .cnav-header {
    min-width: 1000px;
    min-height: 43px;
    margin: 0 -10px;
    background-color: #2a2836!important;
    border-bottom: 1px solid #2a2836!important;
    border-bottom: 0 solid rgba(0, 0, 0, 0);
    -webkit-box-shadow: 0 1px 4px rgba(0, 0, 0, 0.1),0 0 0 1px rgba(0, 0, 0, 0.05);
    box-shadow: 0 1px 4px rgba(0, 0, 0, 0.1),0 0 0 1px rgba(0, 0, 0, 0.05);
    position: relative;
    z-index: 49;
  }
  .ui-toolkit .bg-white {
    background-color: #2a2836;
  }
  .ui-toolkit .catnav-primary .catnav-primary-item.active a, .catnav-primary .catnav-primary-item.active a {
    color: #615fbd;
  }
  .ui-toolkit .catnav-primary .catnav-primary-item:hover > a, .catnav-primary .catnav-primary-item:hover > a {
    color: #615fbd;
  }
  .ui-toolkit .catnav-primary .catnav-primary-item a, .catnav-primary .catnav-primary-item a {
    line-height: 38px;
    text-decoration: none;
    padding: 0 3px;
    color: #7e7dbd;
  }
  .catnav-dropdown aside.catnav-sidebar li.list-nav-item {
    background-color: #2e2c3b!important;
  }
  .catnav-dropdown aside.catnav-sidebar li.list-nav-item.active a {
    color: #e893b7;
  }
  .catnav-dropdown aside.catnav-sidebar li.list-nav-item.active span {
    color: #e893b7;
  }
  .ui-toolkit .text-gray-lightest {
    color: #2a2836;
  }
  .catnav-dropdown aside.catnav-sidebar li.list-nav-item a {
    color: #ffcde3;
    padding-top: 9px;
    padding-bottom: 9px;
    text-decoration: none;
 }
  .ui-toolkit a.text-gray:hover {
    color: #615fbd;
  }
  .ui-toolkit .text-gray {
    color: #7e7dbd;
  }
  .ui-toolkit .text-gray-lighter {
    color: #ffcde3;
  }
  .ui-toolkit a.text-gray-lighter:hover {
    color: #e893b7;
  }
  .ui-toolkit .text-gray-darker {
    color: #58725d;
  }
  .ui-toolkit a.text-gray-darker:hover {
    color: #53dd6c;
  }
  .ui-toolkit a:hover, .ui-toolkit a:focus, .ui-toolkit a:active, .ui-toolkit a.is-active {
   cursor: pointer;
    text-decoration: none!important;
  }
  .ui-toolkit body {
    background-color: #2a2836;
  }
 .position-top.position-absolute.display-block.vesta-hp-search-header-img{
    display: none!important;
  }
  .ui-toolkit .vesta-hp-search-header.vesta-hp-search-header-text-black .vesta-hp-search-header-title-text, .ui-toolkit .vesta-hp-search-header.vesta-hp-search-header-text-black .vesta-hp-search-header-credit {
    color: #fff;
  }
.ui-toolkit .btn-orange {
    background-color: #fe5c51;
    border-color: rgba(0, 0, 0, 0);
    color: #21202b;
  }
.ui-toolkit .btn-orange:hover, .ui-toolkit .btn-orange.is-hovering {
    background-color: #00bcda;
    border-color: rgba(0, 0, 0, 0);
 }.ui-toolkit .card {
    border-radius: 3px;
    background-color: #211e27;
    border: 1px solid #211e27;
    border-bottom-color: #211e27;
    display: inline-block;
    max-width: 240px;
    position: relative;
    width: 100%;
  }
  .content-wrap .content-wrap-inner {
    margin: 28px auto 20px;
    border: 1px solid #21202b!important;
    border-bottom-color: #21202b!important;
    border-radius: 5px;
    background-color: #21202b!important;
    padding: 28px;
}
  .shop-name a {
    color: #ffcde3!important;
  }
  #seller .shop-name {
    font-size: 24px;
    font-family: "Guardian-EgypTT",Arial,sans-serif;
    font-weight: 600;
    line-height: 28px;
    text-shadow: 1px 1px #21202b!important;
    word-wrap: break-word;
  }
  .ui-toolkit {
    color: #58725d!important;
    font-family: "Graphik Webfont","Arial","Helvetica",sans-serif;
    font-size: 14px;
    line-height: 1.4;
    text-align: left;
  }
  #favorite-seller .button-shop-fave {
    outline: 0;
    display: inline-block;
    margin: 0;
    font-size: 13px;
    font-weight: normal;
    text-decoration: none;
    cursor: pointer;
    border-radius: 3px;
    height: 26px;
    line-height: 24px;
    position: relative;
    padding: 0 9px;
    background-color: #1B1E24;
    border: 1px solid rgba(0, 0, 0, 0.15);
    color: #444;
    font-family: Arial,sans-serif;
  }
  #favorite-seller .button-shop-fave:hover {
    background-color: #30283c;
    border-color: rgba(0, 0, 0, 0.2);
  }
.content-wrap .content-wrap-inner {
    margin: 28px auto 20px;
    border: 1px solid #21202b!important;
    border-bottom-color: #21202b!important;
    border-radius: 5px;
    background-color: #21202b!important;
    padding: 28px;
    position: relative;
  }
  .favorites-nag {
    background-color: #21202b;
    border-radius: 3px;
    display: table;
    font-family: "Guardian-EgypTT",Arial,sans-serif;
    font-size: 18px;
    font-weight: 400;
    line-height: 24px;
    margin-bottom: 10px;
    text-align: left;
    color: #7e7dbd;
    position: relative;
    text-shadow: 0 1px 0 #21202b!important;
    width: 100%;
  }
 h1, h2 {
    font-family: "Guardian-EgypTT",Arial,sans-serif;
    width: auto;
    text-shadow: 0 1px 0 #21202b!important;
    color: #3b4453!important;
    text-rendering: optimizeLegibility;
  }
  .favorites-nag h2 {
    color: #ffcde3;
    font-weight: bold;
    font-size: 18px;
    padding-right: 5px;
  }
 #listing-page-cart #listing-page-cart-inner {
    background-color: #21202b!important;
    border-radius: 3px 3px 0 0;
    margin: 0;
    padding: 14px;
    margin-bottom: 10px;
    text-align: left;
    position: relative;
    line-height: 20px;
  }
#related-listings-inner {
    background-color: #21202b;
    padding: 14px;
    border-radius: 3px;
  }
  .fullwidth-cart #primary {
    background: #21202b;
    border-top: 3px solid #21202b;
    border-bottom: 3px solid #21202b;
    width: 100%;
    height: auto;
    min-height: auto;
  }
  #variations .item-variation-option select, #item-stock select {
    font-size: 11px;
    color: #fe5c51;
    line-height: 18px;
    max-width: 350px;
    min-width: 150px;
    height: 22px;
    line-height: 18px;
    padding: 1px;
  }
  body.is-global-nav.fullwidth-cart {
    background: #21202b;
  }
  .listing-buy-box-experiment #listing-page-cart {
    padding: 0px;
    margin-bottom: 0px;
    background-color: #21202b;
  }
  .express-buy-button {
    margin-top: -20px;
    padding: 10px 0 0 0;
    background-color: #21202b;
  }
  .buy-button-experimental {
    background-color: #21202b;
  }
  .btn-primary:hover, .btn-primary.hover {
    background-color: #fe5c51;
    border-color: rgba(0, 0, 0, 0.2);
  }
 .btn-transaction:hover, .btn-transaction.hover {
    background-color: #fe5c51;
    border-color: rgba(0, 0, 0, 0.2);
  }
  .btn-transaction {
    background-color: #53dd6c;
    border-color: rgba(0, 0, 0, 0.1);
    color: #FFF;
  }
  .listing-buy-box-experiment #favoriting-and-sharing {
    padding: 0px;
    background-color: #2a2836;
    border-top: #2a2836 1px solid;
    border-bottom: #2a2836 1px solid;
    border-radius: 0px;
    margin-top: 21px;
    margin-bottom: 21px;
  }
  #fav-box {
    background: #2a2836;
    padding: 8px 8px 10px 8px;
  position: relative;
  }
  .btn-fave:hover, .btn-collect:hover, .btn-listing-follow:hover, .btn-listing-follow.hover {
    background-color: #30283c;
    border-color: rgba(0, 0, 0, 0.2);
  }
  .btn-fave, .btn-collect, .btn-listing-follow {
    outline: none;
    display: inline-block;
    margin: 0;
    padding: 0 9px 0 30px;
    font-size: 13px;
    cursor: pointer;
    border-radius: 3px;
    line-height: 23px;
    height: 25px;
    position: relative;
    font-family: inherit;
    background-color: #2a2836;
    border: 1px solid rgba(0, 0, 0, 0.15);
    color: #444;
  }
.empty-header {
    background: none;
    background-color: #211e27;
    width: 980px;
    border: 1px solid #2a2836;
    height: 150px;
    line-height: 30px;
  }
  .ui-toolkit .body-max-width-980 #content {
    background: #211e27;
    max-width: none;
  }
  .ui-toolkit .bg-gray {
    background-color: #2a2836;
    border-color: #2a2836;
  }
  .ui-toolkit .bg-gray {
    background-color: #2a2836;
    border-color: #2a2836;
  }

  }
